css:
@-moz-document domain("skimfeed.com") {
    body {
        background-color: #282a36;
        color: #f8f8f2
    }
    h2 {
        border-bottom: solid 1px #44475a
    }
    .boxes {
        background-color: #282a36;
        border: solid #44475a 1px
    }
    li {
        color: #708FAE;
        border-bottom: solid 1px #282a36;
    }
    a:link {
        color: #f8f8f2
    }
    a:visited {
        color: #8be9fd
    }
    a:hover {
        color: #ffb86c
    }
    #menubar a:link {
        color: #999
    }
    #menubar a:visited {
        color: #50fa7b
    }
    #menubar a:hover {
        color: #ffb86c
    }
    #amount,
    #amountlinks {
        background-color: #282a36;
        color: #f8f8f2;
    }
    span#optionsbutton {
        color: #ff5555;
        background-color: #282a36;
        padding: 2px;
        text-decoration: underline
    }
    #optionsbutton a {
        color: #000;
    }
    button {
        background-color: #282a36;
    }
    a.siteurls:link {
        color: #bd93f9;
        text-decoration: none
    }
    a.siteurls:visited {
        color: #bd93f9;
        text-decoration: none
    }
    a.siteurls:hover {
        color: #ffb86c;
        text-decoration: underline
    }
    a.siteurls:active {
        color: #000
    }
    a.siteurlshighlight:link {
        color: #bd93f9;
        text-decoration: none
    }
    a.siteurlshighlight:visited {
        color: #bd93f9;
        text-decoration: none
    }
    a.siteurlshighlight:hover {
        color: #ffb86c;
        text-decoration: underline
    }
    a.popurltitle:link {
        color: #ff5555;
        text-decoration: none
    }
    a.popurltitle:visited {
        color: #ff5555;
        text-decoration: none
    }
    a.popurltitle:hover {
        color: #ffb86c;
        text-decoration: underline
    }
    .homelink:link {
        color: #44475a;
        text-decoration: none
    }
    .homelink:visited {
        color: #44475a;
        text-decoration: none
    }
    .homelink:hover {
        color: #44475a;
        text-decoration: underline
    }
    .homelink:active {
        color: #44475a;
        text-decoration: none
    }
    #quicktitle {
        color: #ff5555;
        text-decoration: none
    }
}
