css:
/* vim.org -- Dark Theme by viilo [0.1]
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("vim.org"),
domain("vimdoc.sourceforge.net")
{

body, td, p  {
  background: #111111 !important;
  color: #008866 !important;
}

th {
  background: #222222 !important;
}

em {
  font-style: normal !important;
}

.darkbg {
  background-color: #000000 !important;
}

.lightbg {
  background-color: #111111 !important;
}

.sidebar {
/*  background-color: #f0f0dd; */
  font-size: small;
}

.topheader {
/*  background-color: #cccc99; */
}

.newsdate {
  font-style: normal !important;
  color: #6666cc !important;
}

h1 {
  font-size: medium;
  font-weight: bold;
  color: #CC0066 !important;
}

.txth1 {
  font-size: medium;
  font-weight: bold;
  color: #CC0066 !important;
}

h2 {
  font-size: small;
  font-weight: bold;
  color: #880088 !important;
}

.txth2 {
  font-size: small;
  font-weight: bold;
  color: #880088 !important;
}

h3 {
  font-size: x-small;
  font-weight: bold;
  color: #660066 !important;
}

.txth3 {
  font-size: x-small;
  font-weight: bold;
  color: #660066 !important;
}

a.small {
  font-family: arial,helvetica,sans-serif;
  font-size: 75%;
  text-decoration: none;
  color: #000066 !important;
}

.style1 {
  color: #666666 !important;
}

input {
  font-family: arial,helvetica,sans-serif;
}

textarea {
  font-family: arial, helvetica, sans-serif; 
  width: 100%; 
}

.prompt {
	font-family: arial,helvetica,sans-serif;
	font-weight: bold;
}

.errortext {
	font-family: arial,helvetica,sans-serif;
	color: #cc0000
}

.errorheader {
	font-family: arial,helvetica,sans-serif;
	color: #cc0000;
	font-size: medium;
}

.tableheader {
	font-family: arial,helvetica,sans-serif;
	font-weight: bold;
	background-color: #CCCC99;
}

.rowodd {
	font-family: arial,helvetica,sans-serif;
	background-color: #F0F0DD;
}

.roweven {
	font-family: arial,helvetica,sans-serif;
	background-color: #CCCC99;
}

tr.tableheader th {
	font-family: arial,helvetica,sans-serif;
	background-color: #CCCC99;
	text-align: center;
}


td.checker {
	background-color: #007f00;
	background-image: url(http://vim.sourceforge.net/images/checkerboard.gif);
}

/* remove sponsors banner */
a[href*="/sponsor"], a[href*="iccf-ho"] {
  display: none !important;
}

}
