@-moz-document domain("nicovideo.jp") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#head_search form#head_search_form,

.CrossSearch-form,
.CrossSearch-rowSelector,
.___program-card___NvZzi[data-nicoad-grade=gold] .___nicoad-frame___IAfDx:before,
.___tag-card___P0DFz,
.niconico_ch_banner,

:focus,
header#blomaga_category nav .article_count .count,
menu.pager ul li a,
menu.scopebar li .scope,
section.site_body section.contents_list .nav_search li.selected a,
section.site_body section.contents_list .box_filter
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.seigamenu_txt {
    font-size: 75%!important;
}

#top_popular_illust_box .popular_illust_block__item__info--comment_summary,

.FrameTitleLink-titleText,

header#blomaga_category nav a
{
	font-size: 1.85rem!important;
}

#top_popular_manga_box .popular_manga_block__item__info--title a,
#top_popular_manga_box .popular_manga_block__item__info--nickname,

.ContentsDigestFrame-description,
.NicoNewsContainer-textNewsItemLink,
.TextListFrame-item,
.nico-CommonHeaderRoot .common-header-b4zojx,
.user-name,

body,
header#blomaga_category nav .article_count .count
{
	font-size: 1.52rem!important;
}

#top_popular_illust_box .popular_illust_block__item__info--comment_summary,
#top_popular_manga_box .popular_manga_block__item__info--nickname
{
	line-height: 2.1rem!important;
}

.Tabs-tab {
    line-height: 18px!important;
}

.nico-CommonHeaderRoot .common-header-b4zojx {
    line-height: 13px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.AuroraVisionContainer-ad,
.HeaderContainer-ad,
.PcUniTop350x240MiddleEastAdContainer,
.TopPage-middleBillboardAdSection,
.___comment-area____6yDh,
.___ad-banner___IF4c4,
.___ad-footer___Lrxca,
.___middle-ad___iakfv
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.NicoNewsContainer-photoNews .NicoNewsContainer-commentCount {
    bottom: 15px!important;
    right: -14px!important;
}

#top_pickup_list
 {
    left: 2rem!important;
}

#top_pickup_controller li.next {
    right: -85rem!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#contents_side
{
	margin: 1.2rem!important;
}

#top_pickup_list li {
    margin: 27px 10px 0 0!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.Tabs-tab,
.nico-CommonHeaderRoot .common-header-b4zojx
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

#top_pickup_list li a,
#top_pickup_list li
{
	height: auto!important;
}

#top_pickup_list {
    height: 58rem!important;
}

#top_pickup_area {
    height: 60rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.NicoNewsContainer-photoNews,
.TopPage-section,
.___section___beOSa,
.nico-CommonHeaderRoot .common-header-1nvgp3g,
.nico-CommonHeaderRoot .common-header-1pxv7y0,
.nico-CommonHeaderRoot .common-header-h0l8yl
{
	max-width: none!important;
}

#top_pickup_list_items,

.___site-header___Tobpb:after, .___site-header___Tobpb:before,
.ContentsDigestFrame-description,
.seiga_description
{
	width: auto!important;
}

#content,
#header_cnt,
#top_nicoad_box .nicoad_top_frame,
#top_pickup_area,
#top_seigamenu_area,
#top_pickup_list,

.TopPage-section,
.sg_global_bar,

header#blomaga_category nav,
header.site .head_inner,
section.wrapper,
section.site_body article.content,
section.site_body section.contents_list .box_filter,
section.site_body section.contents_list.detail ul.items
{
	width: 98%!important;
}

#contents_main,

.seigatop #main_area_l {
    width: 83%!important;
}

.NicoNewsContainer-textNewsPhoto,
.___site-local-navigation___o_VEo:after, .___site-local-navigation___o_VEo:before
{
    width: 56px!important;
}

#top_popular_illust_box .popular_illust_block__item {
    width: 23%!important;
}

.common-header-m6vxd {
    width: 33rem!important;
}
}

@-moz-document domain("nicovideo.jp") {
/* Font Settings */


a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.NicoNewsContainer .Tabs-tab.is-active,
.Tabs-tab:hover,

a[href]:hover,
h1,
header#blomaga_category ul.categories li.item:hover span,
header#blomaga_category nav .article_count .count,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
menu.pager ul li:hover a,
menu.pager ul li.selected a,
menu.scopebar li .scope:hover,
menu.scopebar li.selected .scope
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#top_personalize_box--nologin .top_personalize_block__body__message,
#top_popular_illust_box .popular_illust_block__item__info--comment_summary,
#top_popular_manga_box .popular_manga_block__item__info--nickname,

.ContentsDigestFrame-description,
.ContentsPickupCard-metaPickupComment,
.___name-label___yD8f5,
.detail .info.info_article .issue_box span.mylist, .detail .info.info_article .issue_box span.comment,

[class*='common-header-'],
body,
li,
header#blomaga_category nav .article_count .count .unit,
menu.pager ul li a,
menu.scopebar li .scope,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#top_personalize_box--nologin .top_personalize_block__header,
#top_popular_illust_box .popular_illust_block__item__info,

.AuroraVisionCard-metaCatch,
.FrameTitleLink-titleText,
.common-header-yxm7eu,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.AuroraVisionCard-metaPickupComment,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.AuroraVisionCard-metaContentTitle,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.detail .info.info_article .issue_box .date_time,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.detail .info.info_article .issue_box span.mylist.empty, .detail .info.info_article .issue_box span.comment.empty
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("nicovideo.jp") {
/* Background Settings */

.BaseLayout,
body,
html
{
	background: #000!important;
}

.___organization-program-list-carousel___iPXh7
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

#footer,
#header,
#foot_common,

.FooterContainer,
.TopPage-footer,
.nico-CommonHeaderRoot .common-header-654o26,
.nico-CommonHeaderRoot .common-header-cdesjj,
.pankuzu,

footer.site .sns_buttons,
menu.pager ul li a
{
	background: #060505!important;
	background-color: #060505!important;
}

.head_common,

header#blomaga_category
{
    background: #1a1a1a!important;
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#body_main_outer #body_main_inner,
#explore_articles nav,
#head_search,
#recommend_tag .tag_items li a,
#top_news_box .top_news_block__itemlist,
#top_nicoad_box .nicoad_top_frame,
#top_personalize_box--nologin .top_personalize_block__body,
#top_pickup_area,
#top_popular_illust_box .popular_illust_block__item__info--comment_summary,
#top_popular_manga_box .popular_manga_block__item,

.AuroraVisionCard,
.CrossSearch-form,
.CrossSearch-rowSelector,
.Tabs-tab,
.background,
.___base-page___fYQlz,
.___heading___CJvYF,
.___heading___OJYlh,
.___item-list___wLWhZ,
.___konomi-tag-list-section____h0Z2,
.___page-top-anchor___tCUVZ,
.___program-preview___dgczu,
.___ranking-contents-area___JJEgK,
.___site-footer-utility___b5pQU,
.___site-information-list-section___jeb3o,
.___tag-card___P0DFz,
.nico-CommonHeaderRoot .common-header-1pxv7y0,
.nico-CommonHeaderRoot .common-header-12t3fr7:hover,
.pickup_thumnail,
.portal section.site_body article.content,

[class*='common-header-'],
[class*='__title'],
header#blomaga_category nav .item a,
menu.scopebar li .scope,
section.site_body section.contents_list .box_filter,
section.panel
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

#top_personalize_box--nologin .top_personalize_block__body__buttons--login,

.NiconicoTwitterBanner,
.SeeMoreLink-text,
.___program-broadcast-start-anchor___wwYa1,
.___related-anchor___YsMqK,
.common-header-19wda4x
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#top_personalize_box--nologin .top_personalize_block__body__buttons--login::before,

.NiconicoTwitterBanner::before,
.SeeMoreLink-text::before,
.___program-broadcast-start-anchor___wwYa1::before,
.___related-anchor___YsMqK:before,
.common-header-19wda4x::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#top_personalize_box--nologin .top_personalize_block__body__buttons--login:hover::before,

.NiconicoTwitterBanner:hover::before,
.SeeMoreLink-text:hover::before,
.___program-broadcast-start-anchor___wwYa1:hover::before,
.___related-anchor___YsMqK:hover::before,
.common-header-19wda4x:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

#top_personalize_box--nologin .top_personalize_block__body__buttons--register,

.___related-anchor___PaA0q,
.___toggle-button___wlQqy,
.common-header-y0xjq1
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#top_personalize_box--nologin .top_personalize_block__body__buttons--register::before,

.___related-anchor___PaA0q:before,
.___toggle-button___wlQqy:before,
.common-header-y0xjq1::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#top_personalize_box--nologin .top_personalize_block__body__buttons--register:hover::before,

.___related-anchor___PaA0q:hover::before,
.___toggle-button___wlQqy:hover::before,
.common-header-y0xjq1:hover::before
{
  opacity: 1!important;
}

/* Gray 
_____________________________________________________________________________________________________*/

.NicoNewsContainer-commentCount
{	
	background: radial-gradient(circle, #797979a8 0%, #000 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.NicoNewsContainer-commentCount::before
{
	background: radial-gradient(circle, #000 0%, #797979 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.NicoNewsContainer-commentCount:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#contents_side
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#top_personalize_box--nologin .top_personalize_block,
#top_pickup_list li,
#top_popular_illust_box .popular_illust_block__item,

.AuroraVisionContainer-body,
.ContentsPickupCard,
.NicoAdContainer-card[data-nicoad-grade=gold],
.NicoNewsContainer-photoNews,
.___program-card___Z1MHA,
.common-header-1hrl22z,
.nico-CommonHeaderRoot .common-header-m6vxd
{
	background-color: #222!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#contents_side:hover,
#top_personalize_box--nologin .top_personalize_block:hover,
#top_pickup_list li:hover,
#top_popular_illust_box .popular_illust_block__item:hover,

.AuroraVisionContainer-body:hover,
.ContentsPickupCard:hover,
.NicoAdContainer-card[data-nicoad-grade=gold]:hover,
.NicoNewsContainer-photoNews:hover,
.___program-card___Z1MHA:hover,
.common-header-1hrl22z:hover,
.nico-CommonHeaderRoot .common-header-m6vxd:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.common-header-1q7xmj5
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

.NicoAdContainer-card[data-nicoad-grade=gold]>.NicoAdContainer-cardHeader
{
	background: linear-gradient(to bottom, #f3d00a 0%, #0000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.common-header-1q7xmj5
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

#top_popular_manga_box .popular_manga_block__item:hover,

.NicoNewsContainer-textNews>li:hover,
.TextListFrame-contents .TextListFrame-item:hover,
.TicketNoticeContainer-title:hover,
.___item___M8hMc:hover,
.article_style .contents_list.article.detail li.item:hover,

section.contents_list ul.items li.item:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.NicoNewsContainer .Tabs-tab.is-active
{
	background: radial-gradient(ellipse at center, #ff8000 0%,#000 70%)!important;
	padding: 13px 26px!important;
}

.nico-CommonHeaderRoot .common-header-b4zojx:hover
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#000 70%)!important;
	padding: 13px 26px!important;
}

.CrossSearch-rowService.active, .CrossSearch-rowService:hover,

header#blomaga_category nav .article_count .count,
menu.pager ul li.selected a,
menu.scopebar li.selected .scope,
section.site_body section.contents_list .nav_search li.selected a
{
	background: radial-gradient(ellipse at center, #00809d 0%,#000 70%)!important;
}


}

