css:
@-moz-document domain("www.monvoisin.xyz") {
.container, #header, tr
{
    background: #191717 !important;
}
.main, #recent-posts-2, #search-2,
.sidebar-top, #recent-comments-2, #tag_cloud-2,
.main-inner, .post-nav, #ha-large-header
{
	background: #222020!important;
	-webkit-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
}
#header .hu-pad, .notebox
{
    display: none !important;
}
.post-category a
{
    color:#756868!important;
}

.post-category a:hover
{
    color:orange!important;
}
.nav-wrap a, .is-scrolled.header-skin-light #nav-topbar .nav li a
{
    color:#756868!important;
}
.nav-wrap a:hover, .is-scrolled.header-skin-light #nav-topbar .nav li > a:hover 
{
    color:orange!important;
}
div, span
{
    color:#8D7E7E;
}
article p
{
    color: #969696;
}
article p
{
    color: #969696;
}
.entry-inner a
{
    color:#808040;
}
.entry-inner a:hover
{
    color:#008040;
}
p span
{
    color:#005782 !important;
}
blockquote span a:hover
{
    color:#00A5F4 !important;
}
#commentform
{
    background:#191717;
}
.comment-form-comment label, .comment-form-author label,
.comment-form-email label, .comment-form-url label
{
    color:#808040;
}
#comment
{
    background:#756868;
    color:#191717;
    border:none!important;
}
#submit
{
    color:#C0C0C0;
}

#submit:hover
{
    background:#615656;
    color:orange;
}
.post-title a
{
    color:#777700!important;
}
.post-title a:hover
{
    color:#008040 !important;
}

h2.post-title.entry-title:hover
{
    color:red;
}
.wp-playlist-light
{
	background: #756868;
	color: #000;
}
.wp-playlist
{
	border: 1px solid #797979;
}
.wp-playlist-light .wp-playlist-playing
{
	background: #756868;
	color: #000;
}
.wp-playlist-light .wp-playlist-playing a:hover
{
	color: #000;
}
.entry .wp-caption, .entry table tr.alt
{
	background: #756868;
}
.entry .wp-caption-text, .wp-block-image figcaption
{
	color: #191717;
}
.post-tags, .post-tags a
{
	background : #222020;
}
.post-tags a:hover
{
	background : #756868;
    color: #191717;
}
.hu-pad li
{
	color:#808040;
}
input
{
    background:#756868 !important;
    border: 1px solid #191717 !important;
    color: #191717;
}
.hu-pad ul li a:hover
{
    color: orange!important;
}
img
{
    opacity: 0.7;
}

#recent-posts-2 a:hover, #recent-comments-2 a:hover, #tag_cloud-2 a:hover
{
    color:#C0C0C0!important;
}
.search-submit
{
    color:#C0C0C0!important;
}
.search-submit:hover
{
    color:orange!important;
}
.search-field
{
    color:#202022!important;
}

.page-title
{
	background: #4D4848;
	border-bottom: none!important;
	color: #202022;
}
.page-title h1, .page-title h2, .page-title h4
{
	color: #202022;
}
.page-title span
{
	color: #938686;
}

h2.hph-title, p.hph-title
{
	color: #C0C0C0!important;
}
.vcard.author a
{
    color:orange!important;
}

.entry table td,
.entry table th, .post-standard.excerpt .post-inner,
.alx-posts li, .alx-tab li, .widget_archive ul li,
.widget_categories ul li, .widget_links ul li, .widget_meta ul li,
.widget_nav_menu ul li a, .widget_pages ul li a,
.widget_recent_comments ul li, .widget_recent_entries ul li,
.widget_rss ul li
{
	border-bottom: 1px solid #4D4848;
}
address, cite, em
{
	font-style: italic;
    color: #808040 !important;
}
h1, h2, h3, h4, h5, h6
{
	color: #808040;
}

#bottom-welcome-note a,
.comment-body a, .widget_text a,
article .entry a
{
	text-decoration: none;
    color: #008040 !important;
}
#bottom-welcome-note a:hover,
.comment-body a:hover, .widget_text a:hover,
article .entry a:hover
{
    color:  orange !important;
}


}
