css:
@-moz-document url-prefix("https://unboundcloud.net"), url-prefix("https://unboundcloud.net") {
  :root {
    --bs-font-sans-serif: "Inter" !important;  
  }

  body {
    --home-box-color: #97FFC7;
    --save-box-color: #97DCFF;
    --scrollbar-width: 8px;
    --main-background-color: #f2f2f2 !important;
  }

  body, h1, h2, h3, h4, h5, h6, button, input, optgroup, select, textarea {
    font-family: "Inter" !important;
  }

  h2, button {
    font-weight: bold !important;
  }

  button {
    border-radius: 8px !important;
  }

  .top-bar-buttons {
    background-color: #639;
    margin-bottom: 0;
    box-shadow:  0 4px 16px -8px #639A;
    position: fixed;
  }

  .top-bar-button {
    max-width: inherit;
    border-radius: 0 !important;
    width: auto;
    padding: 0 24px;
  }

  .scroll-container {
    inset: 0;
    top: 56px;
    bottom: 48px;
  }

  .footer-buttons {
    position: fixed;
    left: 0;
    right: 0;
    bottom: 0;
    align-items: center;
    height: 48px;
  }

  .footer-buttons div {
    align-items: center;
  }

  .footer-button {
    width: 48px;
    height: 48px;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .footer-button svg {
    width: 32px;
  }

  .main-page-boxes {
    justify-content: center;
    column-gap: 64px;
    padding: 32px 24px;
  }

  .main-page-upload-instructions div:last-of-type {
    display: flex;
  }

  .main-page-upload-instructions div:last-of-type div + div{
    margin-left: 16px;
  }

  .main-page-upload-instructions .choose-home-file-button{
    background-color: #639;
    color: #FFF;
    border: none;
    margin: 0;
    height: 48px;
    padding: 0 16px;
  }

  .btn-success.choose-home-file-button,
  .btn-danger.choose-home-file-button{
    border: 2px solid #333;
    background-color: transparent;
    color: #333;
  }

  .box {
    padding: 16px;
    border-radius: 0;
    margin-bottom: 0;
    grid-column-gap: 4px;
    grid-row-gap: 4px;
    border: 1px solid #0003;
  }

  .home-box,
  .save-box {
    position: relative;
    z-index: 10;
  }

  @media (max-width: 475px) {
    .box {
      padding: 8px;
      grid-column-gap: 2px;
      grid-row-gap: 2px;
    }
  }

  .box-icon {
      width: 68px;
      height: 56px;
  }

  .hover-save-box-icon,
  .hover-home-box-icon {
    outline-offset: 0px;
    outline-style: solid;
    outline-width: 2px;
    outline-color: #000A;
  }

  .box-icon-image {
      max-height: 56px;
  }

  @media (max-width: 475px) {
    .box-icon {
      width: 61px;
      height: 50px;
      min-height: inherit;
    }
    .box-icon-image {
      max-height: 50px;
    }
  }

  .box-icon {
    border: none;
    border-radius: 4px;
    background: #FFFC;
    backdrop-filter: blur(8px) brightness(1.4) ;
  }

  .box-view {
    margin: 0 8px;
  }

  .box-lower-icons,
  .pokemon-summary-container,
  .box-title-no-edit, .box-title-edit {
    box-shadow: 0 4px 16px -8px #6399;
    border: 1px solid #0003;
  }

  .box-title-no-edit, .box-title-edit {
    margin: 0;
    border-radius: 16px 16px 0 0;
    border-bottom: none;
    padding: 0 16px;
    align-items: center;
    height: 56px;
    max-height: 56px;
    position: relative;
    z-index: 10;
  }

  .box-name-button {
    background-color: #639;
    color: #FFF;
    border-radius: 8px;
    box-shadow: 0 2px 16px -4px #0004;
    padding: 4px 16px !important;
  }

  .box-name-cancel-icon,
  .box-name-save-icon {
    margin: 0;
    display: flex;
    inset: 0;
  }

  .box-name {
    margin: 0 8px;
  }

  .box-name-input {
    margin: 0 8px;
    border-radius: 8px;
    border: 1px solid #0003;
    height: 40px;
    inset: 0;
    max-width: 200px;
    font-size: 22px !important;
  }

  .box-lower-icons {
    justify-content: flex-start;
    padding: 0 16px;
    align-items: center;
    border-radius: 0;
    border-top: none;
    border-bottom: none;
    height: 48px;
    margin-bottom: 0;
    position: relative;
    z-index: 10;
  }

  .box-lower-icons:last-of-type {
    border-radius: 0 0 16px 16px;
    border-bottom: 1px solid #0003;
  }

  .box-lower-icon-div {
    margin: 0;
    border-radius: 24px;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 40px;
    width: 40px;
    cursor: pointer;
  }

  .box-lower-icon-div:hover {
    background: #0003;
  }

  .box-lower-icon-div svg {
    width: 24px;
    height: 24px;
  }

  .box-lower-icon-div + .box-lower-icon-div {
    margin-left: 8px;
  }

  .box-lower-icons,
  .pokemon-summary-container,
  .box-title-no-edit, .box-title-edit {
    background: #FEFEFE;
  }

  .home-box {
    box-shadow: 0 4px 16px -4px var(--home-box-color);
  }

  .save-box {
    box-shadow: 0 4px 16px -4px var(--save-box-color);
  }

  .pokemon-summary-container {
    border-radius: 0 0 16px 16px;
    width: 100%;
    padding: 12px;
    margin: 0;
    position: relative;
    z-index: 10;
  }

  .summary-name-level-container {
    font-weight: bold;
    font-size: 18px;
  }

  .summary-stats-container {
    grid-row-gap: 2px;
    border-radius: 8px;
    background: #2e624d1a;
    grid-template-columns: auto 14px auto;
    padding: 6px 12px;
  }

  .summary-stat-values-container {
    align-items: center;
    justify-content: end;
    grid-row-gap: 2px;
  }

  .summary-stat-title {
    font-weight: bold;
    color: #2e624d;
  }

  .male-gender-symbol,
  .female-gender-symbol {
    font-size: 18px;
    font-family: "Fira Code";
  }

  .box-icon-shiny-icon {
    font-size: 16px;
    font-family: "Fira Code";
    bottom: -2px;
  }

  .box-icon-item-icon {
    width: 16px;
    height: 16px;
    image-rendering: pixelated;
    bottom: 2px;
    right: 0;
  }

  .summary-moves-container,
  .summary-stats-container{
    margin-top: 8px;
  }

  .mini-boxes>div {
    flex: 1 0 15%;
  }

  .mini-box {
    grid-row-gap: 2px;
    grid-column-gap: 2px;
    border-radius: 8px;
    padding: 4px;
  }

  .mini-box-cell {
    height: 24px;
    width: 24px;
    border: none;
    border-radius: 4px;
  }

  .mini-box-with-title {
    margin-left: 12px;
    margin-right: 12px;
    margin-bottom: 24px;
  }

  .summary-male-ot
  .summary-female-ot{
    font-weight: 500;
  }


  img.swal2-image {
    margin: 0 auto 16px;
    width: calc(68px * 2);
    image-rendering: pixelated;
  }

  .swal2-icon {
    margin: 0 auto 16px;
  }

  .swal2-container.swal2-center>.swal2-popup {
    padding: 32px 16px 32px;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 4px 16px -8px #000A;
  }

  .swal2-actions {
    padding: 24px 16px;
    border-top: 1px solid #0003;
    background-color: #EEE;
    width: auto;
    margin: 24px -16px -32px;
    display: flex;
  }

  .swal2-styled {
    margin: 0 8px;
    height: 40px;
    padding: 0 16px;
    flex-grow: 1;
  }

  .swal2-styled.swal2-confirm {
    background-color: #639;
  }

  .swal2-styled.swal2-cancel {
    color: #333;
    background-color: transparent;
    border: 2px solid #333;
  }

  .save-box-search,
  .home-box-search {
    color: #333;
  }

  .form-label {
    margin-bottom: 2px;
  }

  .search-form>div {
    margin-top: 12px;
  }

  .search-form.save-box,
  .search-form.home-box {
    border: 1px solid #0003;
    border-radius: 0 0 16px 16px;
    padding: 16px;
  }

  .search-form-buttons {
    padding: 16px;
    background-color: #FFF;
    margin: 0 -16px -16px;
    border-radius: 0 0 16px 16px;
    display: flex;
    border-top: 1px solid #0003;
  }

  .search-button {
    padding: 0 16px;
    height: 40px;
    font-size: 1.25rem;
    width: 100%;
    background-color: #639;
    border: 0;
  }

  .form-control,
  .ui.selection.dropdown {
    border-radius: 8px;
    border: 1px solid #0003;
    height: 40px;
  }

  .ui.selection.active.dropdown .menu {
    border-color: #0003;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    padding: 8px 0;
  }
}
