css:
@-moz-document domain("ipv64.net") {
:root {
	color-scheme: dark;
	--blue-dark: #0056b3;
	--text-danger: hsl(354, 70%, 25%);
	--pink-dark: hsl(332, 50%, 50%);
	--text-dark: #343a40;

	--primary: var(--blue-dark);
	--secondary: var(--dark);
	--danger: var(--text-danger);
	--dark: hsl(210, 10%, 13%);
	--blue: #007bff;
	--pink: var(--pink-dark);
}

body {
	background: hsl(210, 10%, 12%);
	color: var(--light);
}
a {
	color: var(--blue);
}
a:hover {
	color: var(--blue-dark);
}
code {
	color: var(--pink);
}

.dropdown-menu,
.bg-light {
	--light: hsl(210, 17%, 37%);
	background-color: var(--light) !important;
	color: var(--text-dark);
}

.dropdown-item:focus,
.dropdown-item:hover {
	--light: hsl(210, 17%, 27%);
	background-color: var(--light) !important;
	color: var(--dark);
}

.card,
.list-group-item {
	background-color: var(--secondary) !important;
}
.list-group-item {
	color: var(--light);
}
.jumbotron {
	background-color: var(--gray-dark);
}
.card-header {
	background-color: rgb(0 0 0 / 20%)
}
.text-danger {
	color: var(--text-danger) !important;
}
.table {
	color: inherit;
}
.table-primary,
.table-primary > td,
.table-primary > th {
	background-color: var(--gray);
}
.form-control {
	color-scheme: dark;
	background: var(--dark);
	color: var(--gray);
}
.form-control:focus {
	background: var(--dark);
	color: var(--light);
}
.form-control:disabled,
.form-control[readonly] {
	background-color: var(--gray-dark);
	color: var(--gray);
}

.btn-primary {
	background-color: var(--primary);
	border-color: var(--primary);
}
.text-dark {
	color: var(--text-dark) !important;
}
.table-warning,
.table-secondary,
.table-success {
	color: var(--dark);
}
}

@-moz-document url("https://ipv64.net/darkmode.php"), url("https://www.ipv64.net/darkmode.php") {
h1.display-3,
p.lead {
	visibility: hidden;
	position: relative;
}
p.lead::after,
h1.display-3::after {
	display: block;
	visibility: visible;
	position: absolute;
	top: 0;
	left: 0;
}
h1.display-3::after {
	content: "Dark Mode!";
}
p.lead::after {
	content: "Um zum Bright Mode zur√ºckzukehren, deaktiviere das Userstyle \"ipv64.net Dark Mode\" in deinem addon.";
}
}
