    28% {background-color: #8BCE50;border-color: #8BCE50;}
    42% {background-color: #2C2C2C;border-color: #2C2C2C;}
    56% {background-color: #8BCE50;border-color: #8BCE50;}
    80% {background-color: #2C2C2C;border-color: #2C2C2C;}
    94% {background-color: #8BCE50;border-color: #8BCE50;}
    100% {background-color: #2C2C2C
;border-color: #2C2C2C;}
}

