/* ==UserStyle==
@name           Dracula Spotify
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Spotify Theme that follows Dracula guidelines
@author         TaylorTek
@license        GNU GPLv3
==/UserStyle== */

@-moz-document domain("spotify.com") {
    :root {
        --dracBG: rgba(40, 42, 54, 100%);
        --dracBG2: rgba(30, 32, 44, 100%);
        --dracGreen: rgba(80, 250, 123, 100%);
        --dracWhite: rgba(248, 248, 242, 100%);
        --dracPink: rgba(255, 121, 188, 100%);
        --dracPurple: rgba(189, 147, 249, 100%);
    }

    /*Top Bar*/
    .Root__main-view, .HsbczDqu9qjcYr7EIdHR {
        background-color: var(--dracBG)!important;
        background-image: none;
    }
    
    /*Top Bar Scrolled*/
    .T1xI1RTSFU7Wu94UuvE6 {
        background-color: var(--dracBG)!important;
    }
    
    .EvIR4O7jOSbNmxtMdIQ0 {
        display: none;
    }
    
    /*Play Bar*/
    .GD2gbRtcs5dOjMGAM_Y4 {
        background-color: var(--dracBG2)!important;
    }
    
    /*Side Menu*/
    .tUwyjggD2n5KvEtP5z1B, .Y8edH1Yjo4xrW_58czQj {
        background-color: var(--dracBG2);
        background-image: none;
    }
    
    /*Top background of playlist*/
    .CoLO4pdSl8LGWyVZA00t, .gHImFiUWOg93pvTefeAD, .contentSpacing, .koyeY6AgGRPmyPITi7yO {
        background-color: var(--dracBG)!important;
        background-image: none!important;
    }
    
    /*Song Panels*/
    .LunqxlFIupJw_Dkx6mNx, .LunqxlFIupJw_Dkx6mNx:hover {
        background-color: var(--dracBG);
    }
    
    /*Play Buttons*/
    .ButtonInner-sc-14ud5tc-0 {
        background-color: var(--dracPurple)!important;
    }
    
    /*Shuffle Button*/
    .OF_3F0SQCsBtL1jSTlTA {
        color: var(--dracPink)!important;
    }
    
    /*Scrub Bar Fill*/
    .epWhU7hHGktzlO_dop6z {
        background-color: var(--dracPurple)!important;
    }
    
    /*Scrub Bar Background*/
    .NoOAOv6U6vtqj_ybS1Cd {
        background-color: var(--dracBG2);
    }
    
}
