css:
@-moz-document domain(www.instagram.com) {

	/* Delete image overlay so you can easily copy/paste the image now */
	div[role="button"] div div div:nth-child(2) {
		display: none;
		opacity: 0;
		position: absolute;
		left: 0;
		top: 0;
	}
/* Main Dark Mode */

/* Everything dark */
div, section {
    --ig-primary-background: 35 35 39 !important;
    --ig-secondary-background: 35 35 39!important;
}
input div {
    --ig-banner-background: 35 35 39 !important;
}

/* White text */
a,p,h1,h2,h3,h4,h5,h6,li,span {
    color:  #fff !important;
}

/* Visible SVG icons */
button[type="button"] div svg, nav div svg {
    background-image: linear-gradient(#ff45e7,#ffc300,#f60000) !important;
    border-radius: 5px !Important;
    border: 2px solid #ffffff;
}

/* Search icon removal */
nav div[role="button"] {
    display: none !important;
}


}
