css:
@-moz-document domain("animeflv.net") {
  :root {
    --base-100: rgb(52, 55, 70);
    --base-200: rgb(51, 54, 68);
    --base-300: rgb(33, 34, 44);
    --base-content: rgb(248, 248, 242);

    --primary: rgb(255, 122, 198);
    --primary-active: rgb(255, 46, 164);
    --primary-content: rgb(77, 0, 43);

    --secondary: rgb(191, 149, 249);
    --secondary-active: rgb(144, 72, 244);
    --secondary-content: rgb(34, 0, 82);

    --accent: rgb(255, 184, 107);
    --accent-active: rgb(255, 149, 36);
    --accent-content: rgb(71, 37, 0);

    --success: rgb(82, 250, 124);
    --success-content: rgb(0, 66, 17);

    --danger: rgb(255, 87, 87);
    --danger-content: rgb(66, 0, 0);

    --btn: rgb(55, 58, 74);
    --btn-active: rgb(52, 55, 70);
    --btn-content: rgb(194, 202, 245);

    --btn-ghost: rgb(0, 0, 0, 0);
    --btn-ghost-active: rgba(248, 248, 242, 0.2);
    --btn-ghost-content: rgb(248, 248, 242);
  }

  * {
    color: var(--base-content) !important;
  }

  .NwBxCn figure span {
    color: #000000 !important;
  }

  .BX {
    flex-direction: column;
  }

  .Sidebar {
    width: 100% !important;
  }

  /* ---------------------------- EPISODIOS DE HOY ---------------------------- */
  .ListEpisodios {
    display: grid !important;
    grid-template-columns: repeat(2, minmax(0, 1fr)) !important;
  }
  @media (min-width: 640px) {
  }
  @media (min-width: 768px) {
    .ListEpisodios {
      grid-template-columns: repeat(4, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 1024px) {
  }
  @media (min-width: 1280px) {
  }
  @media (min-width: 1536px) {
    .ListEpisodios {
      grid-template-columns: repeat(5, minmax(0, 1fr)) !important;
    }
  }

  .ListEpisodios > li {
    width: 100% !important;
  }

  .ListEpisodios .Image img,
  .Anime .Image img {
    position: relative !important;
  }
  .ListEpisodios .Image,
  .Anime .Image {
    padding-top: 0 !important;
  }

  .Anime.alt.B .Image:after {
    top: 0px !important;
    left: 0px !important;
    width: 100% !important ;
    height: 100% !important;
    background-color: rgba(144, 72, 244, 0.5) !important;
  }

  .Anime .Image img {
    left: 0 !important;
    top: 0 !important;
    width: 100% !important;
    height: 100% !important;
    max-width: 100% !important;
    max-height: 100% !important;
  }

  /* ---------------------------- LISTADO DE ANIMES --------------------------- */
  .ListSdbr {
    display: grid !important;
    grid-template-columns: repeat(1, minmax(0, 1fr)) !important;
  }

  @media (min-width: 640px) {
    .ListSdbr {
      grid-template-columns: repeat(2, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 768px) {
    .ListSdbr {
      grid-template-columns: repeat(3, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 1024px) {
    .ListSdbr {
      grid-template-columns: repeat(4, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 1280px) {
  }
  @media (min-width: 1536px) {
    .ListSdbr {
      grid-template-columns: repeat(5, minmax(0, 1fr)) !important;
    }
  }

  /* ------------------------- LISTA DE ULTIMOS ANIMES ------------------------ */
  .ListAnimes {
    display: grid !important;
    grid-template-columns: repeat(3, minmax(0, 1fr)) !important;
  }
  @media (min-width: 640px) {
  }
  @media (min-width: 768px) {
    .ListAnimes {
      grid-template-columns: repeat(4, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 1024px) {
  }
  @media (min-width: 1280px) {
    .ListAnimes {
      grid-template-columns: repeat(6, minmax(0, 1fr)) !important;
    }
  }
  @media (min-width: 1536px) {
    .ListAnimes {
      grid-template-columns: repeat(8, minmax(0, 1fr)) !important;
    }
  }

  .ListAnimes > li {
    width: 100% !important;
  }

  .Sidebar {
    order: 2;
  }

  .Sidebar > a {
    display: none;
  }

  .Sidebar > br {
    display: none;
  }

  .Wdgt,
  .WdgtCn,
  .CapiTnv > li > a {
    background-color: var(--base-300) !important;
  }

  .CapiTnv > li.active > a {
    box-shadow: inset 0 2px 0 #ff7ac2;
  }

  .Header {
    position: sticky !important;
    top: 0px !important;
    background-color: var(--base-300) !important;
  }

  .Header > .Mid {
    background-color: rgba(0, 0, 0, 0);
  }

  .Header > .Mid .Logo {
    filter: hue-rotate(1200deg);
  }

  .Footer {
    background-color: var(--base-300) !important;
  }

  .Body {
    background-color: var(--base-200) !important;
  }

  /* ------------------------------ BOTONES ANIME ----------------------------- */

  .BtnNw i {
    color: var(--btn-content) !important;
  }

  .BtnNw.Dwnd,
  .BtnNw.Dwnd::before,
  .BtnNw.Dwnd span {
    color: var(--success-content) !important;
    background-color: var(--success) !important;
  }

  .BtnNw.Rprt,
  .BtnNw.Rprt::before,
  .BtnNw.Rprt span {
    color: var(--danger-content) !important;
    background-color: var(--danger) !important;
  }
  .CapNv {
    background-color: var(--base-300) !important;
  }

  .BtnNw.CVst,
  .BtnNw.Clgt,
  .BtnNw.Xpnd {
    color: var(--btn-content) !important;
    background-color: var(--base-100) !important;
  }

  .Anime.alt .Image {
    background-color: rgba(0, 0, 0, 0);
  }

  .Capi {
    color: rgb(77, 0, 43) !important;
    background-color: var(--primary) !important;
    border-color: var(--primary) !important;
  }

  .Type.tv {
    color: rgb(71, 37, 0) !important;
    background-color: var(--accent) !important;
    border-color: var(--accent) !important;
  }

  .Estreno {
    left: 0px;
    top: 0px;
  }

  .Estreno:before,
  .Estreno:after {
    display: none !important;
  }

  .Estreno > span {
    color: rgb(34, 0, 82) !important;
    background-color: var(--secondary) !important;
    border-color: var(--secondary) !important;
  }

  .Anime.alt.B .Description {
    background-color: var(--base-300) !important;
  }

  /* ---------------------------------- V 1.1 --------------------------------- */

  .ListResult {
    background-color: var(--base-300) !important;
  }

  .btn-default {
    color: var(--btn-content) !important;
    background-color: var(--btn) !important;
  }

  .caret::before {
    color: var(--btn-content) !important;
  }
  .dropdown-menu {
    background-color: var(--base-300) !important;
  }
  .dropdown-menu::before {
    border-bottom: 5px solid var(--base-300) !important;
  }

  .filters .multiselect-Container > li > a > label:before {
    color: var(--base-100) !important;
  }

  .filters .btn-group.open > button,
  .filters .btn-group button:hover,
  .filters .btn-group button:focus {
    color: var(--btn-content) !important;
    background-color: var(--btn-active) !important;
  }

  .btn-primary {
    color: var(--primary-content) !important;
    background-color: var(--primary) !important;
  }
  .btn-primary:hover {
    color: var(--primary-content) !important;
    background-color: var(--primary-active) !important;
  }

  .NvCnAnm .pagination span,
  .NvCnAnm .pagination a {
    color: var(--primary-content) !important;
    background-color: var(--primary) !important;
  }

  .NvCnAnm .pagination .active a,
  .NvCnAnm .pagination li:hover a {
    color: var(--primary-content) !important;
    background-color: var(--primary-active) !important;
  }

  .BtnMenu:before {
    background-color: var(--primary) !important;
  }
  #BtnMenu:checked ~ label:before {
    background-color: var(--primary-active) !important;
  }

  .filters .multiselect-container > li.active > a,
  .filters .multiselect-container > li.active > a > label:before {
    color: var(--primary) !important;
  }

  .Title.Page h2 {
    font-size: 2.5rem /* 36px */ !important;
    line-height: 2.5rem /* 40px */ !important;
    font-weight: 300 !important;
    text-align: center !important;
    padding-top: 1rem /* 40px */ !important;
    padding-bottom: 1rem /* 40px */ !important;
  }
  .Title.Page.fa-star {
    padding-left: 0px !important;
  }

  .Order,
  .Title.Page::before,
  .Title.Page::after {
    display: none !important;
  }

  .Container {
    width: 100% !important;
    padding-right: 0.5rem !important;
    padding-left: 0.5rem !important;
  }
  @media (min-width: 640px) {
    .Container {
      max-width: 640px !important;
      padding-right: 1rem !important;
      padding-left: 1rem !important;
    }
  }
  @media (min-width: 768px) {
    .Container {
      max-width: 768px !important;
      padding-right: 2rem !important;
      padding-left: 2rem !important;
    }
  }
  @media (min-width: 1024px) {
    .Container {
      max-width: 1024px !important;
      padding-right: 3rem !important;
      padding-left: 3rem !important;
    }
  }
  @media (min-width: 1280px) {
    .Container {
      max-width: 1280px !important;
      padding-right: 4rem !important;
      padding-left: 4rem !important;
    }
  }
  @media (min-width: 1536px) {
    .Container {
      max-width: 1536px !important;
      padding-right: 5rem !important;
      padding-left: 5rem !important;
    }
  }
  /* ----------------------------- COMPATIBILIDAD ----------------------------- */

  #MalData select option {
    color: var(--base-content) !important;
    background-color: var(--base-300) !important;
  }
}
