/* ==UserStyle==
@name           Qbittorrent Web UI Dark
@namespace      github.com/openstyles/stylus
@version        1.0
@description    Qbittorrent Web GUI - Forked from https://userstyles.org/styles/152766/qbittorrent-dark-webui by Cozzy
@author         Jho
==/UserStyle== */

@-moz-document url-prefix("https://url.domain/qbittorrent/") {
    :root {
        --bg: #11111b;
        --surface: #1e1e2e;
        --alt: #292c3c;
        --text: #6d7088;
        --title: #cdd6f4;
        --selected: #cdd6f4;
        --green: #c1e6e0;
        --yellow: #e5c890;
        --selected_column: #f4e2b3;
        --hover: #94e2d5;
    }

    html,
    body,
    #desktopHeader,
    #desktopNavbar,
    #mochaToolbar,
    #Filters {
        background: var(--bg) !important;
        color: var(--text);
    }

    a {
        color: rgb(30, 160, 210) !important;
    }

    #desktopNavbar {
        border-bottom: none;
    }

    #desktopNavbar li a {
        color: var(--title) !important;
    }

    #desktopNavbar li a:hover {
        background: var(--alt) !important;
    }

    #desktopNavbar li ul {
        background: rgb(40, 50, 70) !important;
        background-image: none !important;
    }

    #desktopNavbar li ul .divider {
        border-top-color: #1a2130 !important;
    }

    #pageWrapper {
        border-top-color: var(--alt) !important;
    }

    #pageWrapper {
        border-top: 1px solid var(--alt);
        border-bottom: 1px solid var(--alt);
    }

    .torrentTable,
    .dynamicTable {
        border-color: #1a2130 !important;
    }

    .torrentTable th,
    .dynamicTable th {
        background: var(--alt) !important;
        color: var(--title) !important;
    }

    .dynamicTable th {
        border-right-color: #1c2538 !important;
    }

    .torrentTable tr,
    .dynamicTable tr {
        background: var(--surface) !important;
    }

    .torrentTable tr:nth-child(even),
    .dynamicTable tr:nth-child(even) {
        background: var(--bg) !important;
    }

    .torrentTable tr.selected,
    .torrentTable tr:hover,
    .dynamicTable tr.selected,
    .dynamicTable tr:hover {
        color: var(--text) !important;
    }

    .torrentTable tr.selected,
    .dynamicTable tr.selected {
        background: var(--selected_column) !important;
    }

    .torrentTable tr:hover,
    .dynamicTable tr:hover,
    ul.filterList li:hover {
        background: var(--hover) !important;
    }

    .torrentTable a:hover,
    .dynamicTable a:hover,
    ul.filterList a:hover {
        color: var(--bg) !important;
    }

    .toolbarTabs {
        padding-bottom: 0 !important;
    }

    .toolbarTabs li,
    .toolbarTabs li a {
        background-image: none !important;
        color: var(--title) !important;
    }

    .toolbarTabs li {
        margin-left: 2px !important;
        margin-right: 2px !important;
        margin-bottom: 0 !important;
        background: rgb(40, 50, 70) !important;
        border-radius: 2px 2px 0 0;
        box-shadow: 0 0 7px #111;
        background-position: left -70px;
    }

    .toolbarTabs li.selected {
        background: rgb(70, 80, 100) !important;
    }

    .toolbarTabs li a {
        margin-left: 0 !important;
        padding: 4px 9px !important;
    }

    #propertiesPanel_header {
        height: 27px !important;
    }

    #propertiesPanel_headerContent {
        padding-top: 3px !important;
    }

    #prop_general fieldset,
    #watched_folders_tab tr,
    #watched_folders_tab th,
    #watched_folders_tab td {
        border-color: #999 !important;
    }

    #desktopFooter {
        background: rgb(40, 50, 70) !important;
        color: var(--title) !important;
    }

    .panel {
        background: var(--surface) !important;
        border-bottom-color: #1c2538 !important;
        color: var(--text) !important;
    }

    .panel-header,
    .panel-headerContent,
    .toolbarTabs {
        background: var(--surface) !important;
		background-image: none !important;
    }

    .panel-header {
        border-bottom-color: #1c2538 !important;
    }

    .horizontalHandle,
    .columnHandle {
        background: var(--alt) !important;
        border-color: var(--alt) !important;
    }

    .columnHandle:after {
        transform: rotate(90deg) !important;
    }

    .horizontalHandle {
        background-image: none !important;
    }

    .mochaCanvas {
        display: none !important;
    }

    .mochaTitlebar,
    .mochaTitlebar.replaced {
        background: #191e2d !important;
        border-radius: 7px 7px 0 0;
    }

    .mochaTitle {
        color: var(--text) !important;
    }

    .mochaToolbarWrapper {
        border-top-color: #aaa !important;
    }

    .mochaToolbar {
        border-top-color: #1c2538 !important;
        background: var(--surface) !important;
        padding-top: 4px;
    }

    #mochaToolbar .divider {
        background-image: none;
    }

    .mochaContentBorder {
        border-top-color: #1c2538 !important;
        border-bottom-color: #191e2d !important;
        border-bottom-width: 23px !important;
        border-radius: 0 0 7px 7px;
    }

    .mochaContentWrapper {
        background: var(--surface) !important;
    }

    .mochaContent fieldset {
        border-color: #1c2538 !important;
    }

    .mocha::after {
        content: "";
        display: block;
        position: absolute;
        top: 4px;
        left: 5px;
        bottom: 7px;
        right: 5px;
        border-radius: 7px;
        box-shadow: 0 0 10px #000;
        z-index: -1000;
    }

    .mocha#preferencesPage::after {
        bottom: 6px;
    }

    .contextMenu,
    .contextMenu li ul {
        background: rgb(40, 50, 70) !important;
        border-color: #000 !important;
    }

    .contextMenu li a {
        color: var(--text) !important;
    }

    .contextMenu li a:hover {
        background: #1b2334 !important;
    }

    h2,
    h3 {
        color: var(--text);
    }

    .filterList a {
        color: var(--text) !important;
    }

    .selectedFilter {
        background-color: #313244;
    }

    .selectedFilter a {
        color: var(--selected) !important;
    }

    /*Adjusts images in the footer and filter menu and torrents section (e.g. the downloading, seeding and paused icons) to be slightly brighter*/
    #filtersColumn img,
    #transferList_wrapper img,
    #desktopFooter img {
        filter: brightness(1.5);
    }

    /* % of download completed*/
    .progressbar_dark {
        background: var(--green) !important;
        color: #302e40 !important;
    }

    /* % of download uncompleted*/
    .progressbar_light {
        background: #FDD !important;
    }

    .panel-collapse {
        background: url(http:../theme/go-down) center center no-repeat;
        background-size: 24px;
    }

    select {
        background: var(--alt);
        border-color: #000;
        color: #eee !important;
    }

    input,
    textarea {
        background: var(--bg) !important;
        color: var(--text) !important;
        border: 0px;
        white-space: nowrap;
    }

    input::placeholder {
        color: #aaa !important;
    }
	
	input[id=password], input[id=username]  {
            background-color: var(--alt)!important;
         }
	input[id=login] {
		border: 1px solid var(--alt)!important;
    	padding: 2px 12px;
	}

    #urls {
        background: var(--alt) !important;
    }

    /* Main tabs */
    #mainWindowTabs {
        float: right;
        margin: 0px;
        padding: 0;
        font-size: 12px !important;
    }

    .tab-menu li.selected a {
        font-weight: normal !important;
    }

    .toolbarTabs li {
        background: var(--bg) !important;
    }

    .toolbarTabs li,
    .toolbarTabs li .selected {
        background: var(--alt) !important;
        margin-left: 2px !important;
        margin-right: 2px !important;
        margin-bottom: 0 !important;
        border-radius: 0;
        box-shadow: none !important;
    }

    .toolbarTabs li a {
        margin: 0 !important;
        padding: 4px 12px !important;
    }

    #prop_general fieldset,
    #watched_folders_tab tr,
    #watched_folders_tab th,
    #watched_folders_tab td {
        border-color: var(--alt) !important;
    }

    #torrentFilesFilterToolbar,
    #torrentsFilterToolbar {
        margin-right: 0;
    }

    #torrentsFilterInput {
        padding: 4px !important;
        border: solid 1px var(--alt);
        font-size: 12px !important;
    }

    #torrentFilesFilterInput {
        padding: 4px !important;
        border: solid 1px var(--alt);
        font-size: 12px !important;
    }

    #downloadPage,
    #downloadPage_overlay,
    #downloadPage_contentWrapper,
    #downloadPage_iframe {
        height: 600px !important;
    }

    #uploadPage,
    #uploadPage_overlay,
    #uploadPage_contentWrapper,
    #uploadPage_iframe {
        height: 420px !important;
    }

    #error_div {
        color: #0f0;
    }

    td.statusBarSeparator {
        background-image: none !important;
    }

    /* scrollbar */

    ::-webkit-scrollbar {
        height: 4px;
        width: 4px;
    }

    ::-webkit-scrollbar-track {
        background-color: var(--alt);
        border-radius: unset;
    }

    ::-webkit-scrollbar-thumb {
        background: var(--yellow);
    }

    ::-webkit-scrollbar-thumb:hover {
        background: var(--yellow);
    }
}
