css:
@-moz-document domain('filelist.io') {

  html {
   background: url('https://miro.medium.com/max/640/1*bNfxs62uJzISTfuPlOzOWQ.png') no-repeat center center fixed;
   -webkit-background-size: cover;
   -moz-background-size: cover;
   -o-background-size: cover;
   background-size: cover;
  }

   body {
     background: none;
   }

  div#container {
    padding-top:0px;
  }

  select[name="forumid"] {
    background-color:rgba(44, 23, 79, 0.3);
  }

  select[name="forumid"]:hover {
    background-color:rgba(44, 23, 79, 0.3);
  }

  input[value="Quick Reply"]{
    width:71px !important;
    height:auto !important;
  }

  input[value="Full Reply"]{	
    width:71px !important;
    height:auto !important;
  }

  input[value="Search"]{	
    width:71px !important;
    height:auto !important;
  }

  input[value="Tags"]{	
    width:71px !important;
    height:auto !important;
  }

  select[id="tcat"] {
    background-color:rgba(44, 23, 79, 0.3);
  }

  select[id="tcat"]:hover {
    background-color:rgba(44, 23, 79, 0.3);
  }

  option[value="0"],
  option[value="1"],
  option[value="2"],
  option[value="3"],
  option[value="4"],
  option[value="5"] {
    color:black;
  }

  p {
    display:flex;
    align-items:center;
    justify-content:space-between;
  }

  div[class="logo"] {
    position-left:-30px;
  }

  div.cblock {
    margin-left:4px !important;
  }

  div.cblock-content {
    width:991px;
      padding-top:9px;
  }

  div.cblock-innercontent{
      padding-top:10px;  
  }

  div.nblock {
    margin-left:5px !important;
  }

  div.nblock-bottom {
    background: rgba(44, 23, 79, 0.3);
  }

  div.cblock-header {
    width:991px;
    margin-top:4px;
    border-left:1px solid;
    border-right:1px solid;
    border-color: #4f2b8d;
    display:flex;
    justify-content:center;
  }

  div.cblock-headerleft {
    width:0px;
    height:0px;
  }

  div.cblock-headerright {
    width:0px;
    height:0px;
  }

  div.sblock {
    width:260px;
  }

  div.sblock-header{
    width:277px;
  }

  div.sblock-headerleft {
    width:0px;
    height:0px;
  }

  div.sblock-headerright {
    width:0px;
    height:0px;
  }

  div.sblock-content {
    width:277px;
  }

  input.btn {
    width:100px;
    height:36px;
  }

  input.btn:hover {
    width:100px;
    height:36px;
  }

  div [style="float:right;padding: 4px 10px 4px 0px;"] > input.btn {
    width:71px !important;
    height:auto !important;
  }

  div#nav {
      display:flex;
    align-content:center;
    justify-content:center;
  }

   .wrapper {
     background: none;
  }

  .subheader {
    display:flex;
    justify-content:space-between;
  }

  .status_avatar > a > img {
    border: 3px solid;
    border-color: #5f35b;
    width:67px;
    height:67px;
  }

  .statusbar {
      display:flex;
    justify-content:space-between;
  }

   .mainheader {
     background: none;
   }

   .nblock-content a {
     color: #39cd2a;
   }

   #navigation {
     background-image: linear-gradient(#3d1f65, #271348);
     border: none;
     box-shadow: none;
       padding: 10px 10px 10px 10px;
   }

   #navigation li a {
     color: #f06816!important;
                         text-shadow: 1px 1px 1px #111;
   }

   #navigation li a:hover {
     color: white !important;
                         text-shadow: 1px 1px 1px #111;
   }


   .torrenttable a {
     color: #f06816!important;
     text-shadow: 1px 1px 1px #111;
   }

   .cblock-innercontent table form {
     padding: 10px 10px 10px 10px!important;
   }

   .noborder input,select {
     background: #222;
     border: 1px solid #5f35b2;
     padding: 10px 1px 10px 10px;
     color: #fff;
   }

     .noborder input:hover,select:hover {
     background: #222;
     border: 1px solid #5f35b2;
     padding: 10px 1px 10px 10px;
     color: #fff;
   }

   .statusbar {
     border: 1px solid #2c174f;
     background: rgba(44, 23, 79, 0.8);
     padding:10px;
   }

   .statusbar a {
     color: #f06816;
   }

  a[href="/index.php"] > .logo {
     width: 400px;
     height: 180px;
     position: relative;
     top: -10px;
     left: 80px;

   }

   #container a {
     color: #f06816;
   }

  .cblock-top, .cblock-bottom {
  display: none;
  }

  .cblock-content {
  border-left: 1px solid #4f2b8d;  
     border-right: 1px solid #4f2b8d;
  }

  .cblock-content, .cblock-innercontent {
     background: rgba(44, 23, 79, 0.8);
  }

  .nblock-content {
  background: rgba(44, 23, 79, 0.3);  
  }

  .sblock-content {
  background: rgba(44, 23, 79, 0.8);  
  }

  .colhead {
    background: rgba(44, 23, 79, 0.8);
    border: 1px solid #6c39ae;
  }

  .nblock-headerleft, .nblock-header, .nblock-headerright {
  background: rgba(44, 23, 79, 0.9);    
  }


  .sblock-headerleft, .sblock-header, .sblock-headerright {
  background: rgba(44, 23, 79, 0.9);    
  }

  .cblock-headerleft, .cblock-header, .cblock-headerright {
  background: rgba(44, 23, 79, 0.9);    
  }


  .colhead {
  background: rgba(44, 23, 79, 0.9);    
  }

  div#footer {
    background-color: rgba(44, 23, 79, 0.8);
  }

  div#copyright{
   background-color: rgba(44, 23, 79, 0.8);
  }
}
