css:
@-moz-document domain(portal.edu.asu.ru) {
body {
  --back: #333333;
  --border: #51565C;
  --text: #f9f9f9;
  --link: #71aaeb;
  --linkHov: #3887E0;
}

*::-webkit-scrollbar {
  width: 12px;
}

*::-webkit-scrollbar-track {
  background: var(--back);
}

*::-webkit-scrollbar-thumb {
  background-color: var(--text); 
  border-radius: 20px;
  border: 3px solid var(--back);
}

* {
  background: var(--back);
  color: var(--text);
  scroll-behavior: smooth;
}

span {
	color: var(--link) !important;  
}

.site-name {
  margin-left: 10px;
  vertical-align: middle;
}

.btn-primary {
  border-color: var(--text);
}

.btn-primary:hover {
  border-color: var(--border);
}

#region-main {
  background: var(--back);
}

.navbar-bootswatch {
  background: var(--back);
}

.navbar-light .navbar-nav .nav-link, .navbar-bootswatch .navbar-nav .nav-link {
  color: var(--text);
}

#username {
  margin-left: 0;
}

#password {
  margin-left: 0;
}

a {
  color: var(--link);
  transition: .1s;
}

a:hover {
  color: var(--linkHov);
  text-decoration: none;
}

a:focus {
  color: var(--linkHov) !important;
  background: none !important;
  box-shadow:  none !important;
  outline: none !important;
}

.card {
  border-color: var(--border);
}

.forumpost.unread .row.header, .path-course-view .unread, span.unread {
  background: none;
}

.dropdown-divider {
  border-top-color: var(--border);
}

#detail-icon-img {
  display: none;
}

.dropdown-item:hover {
  background: var(--back);
  color: var(--link);
}

.dropdown-item:focus {
  color: var(--text);
}

.border-left-0 {
  border-left: 1px solid var(--border) !important;
}

.input-group-text {
  border: 1px solid var(--border) !important;;
  border-radius: .25rem !important;
}

.border {
  border-color: var(--border) !important;
}

.form-control {
  border: 1px solid var(--border) !important;
}

.form-control {
  background: var(--back);
  margin-left: 15px;
}

.bg-light {
  background: var(--back) !important;
  color: var(--text) !important;
}

.form-control:disabled {
  background: var(--back);
  margin-left: 15px;
}

.dropdown-menu {
  background: var(--back);
}

.dropdown-item {
  color: var(--text);
}

.dropdown-item:focus {
  color: var(--text) !important;
}

.nav-link {
  color: var(--text) !important;
}

.nav-link:hover {
  color: var(--link) !important;
}

.nav-link:focus {
  box-shadow: none;
}

.nav-link:focus {
  color: var(--text) !important;
}

.btn-secondary {
  background: var(--linkHov);
  color: var(--text) !important;
  transition: .2s;
}

.btn-secondary:hover {
  background: var(--link);
  color: var(--text);
}

rect {
  fill: var(--back) !important;
}

.list-group-item {
  background: var(--back) !important;
}

path {
  fill: var(--back) !important;
  stroke: var(--text) !important;
}

.bg-dark {
  background: var(--back) !important;
}

.btn-light {
  background: none;
}

.forumpost.unread .row.header {
  border-bottom: none;
}

.no-overflow img {
  display: none;
}

#yui_3_17_2_1_1667837083961_91 {
  margin-top: 30px;
}

.mr-1 {
  margin-right: 7px !important;
}

.caret {
  display: none;
}

td {
  background: var(--back) !important;
  color: var(--color) !important;
}

td:hover {
  color: var(--color) !important;
}

input {
  color: var(--text) !important;
}

input:focus {
  background: var(--back) !important;
}

select {
  background: var(--back) !important;
  color: var(--text) !important;
  border-color: var(--border) !important;
}

.content-item-container.unread {
  padding: 5px 10px;
  background-color: var(--back) !important;
}

.btn-link:focus {
  box-shadow: var(--back) !important;
}

.content-item-container {
  padding: 5px 10px;
  background-color: var(--back) !important;
  border-color: var(--border) !important;
}

.badge {
  margin-top: 5px;
}

.btn {
  background: var(--back) !important;
}

.card-header {
  border-color: var(--border) !important;
}

.content-item-container:hover {
  background-color: var(--back) !important;
}

.card {
  background: var(--back) !important;
}

.popover-region-header-container {
  border-color: var(--border) !important;
}

.message-app .message.received {
  background: var(--back) !important;
}

.popover-region-container {
  border-color: var(--border) !important;
}

.popover-region-footer-container {
  border-top: var(--back) !important;
  background: var(--back) !important;
}

.message-app .day {
  color: var(--text) !important;
}

[data-region=right-hand-drawer].drawer {
  box-shadow: none !important;
}

.message-app .message.received .tail {
  border-bottom-color: var(--back) !important;
}

.bg-white {
  background: var(--back) !important;
}
}
