css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("4chan.org") {

	body { background: #557890 !important; color: #486C96 !important; }

	* { font: 10pt "Sans"; }

	a { color: #48667C !important; }

	hr { display: none !important; }

	#navtop, #navbot { background: #5A88A8; color: #5A88A8 !important; width: 100%; text-align: center; height: 12pt; font-size: 7pt !important; }

	#navtop *, #navbot * { color: #000; font-size: 7pt; display: inline; }

	#navtop a:hover, #navbot a:hover { background: #2A4E68; }

	form { margin: 1em; }

	.inputtext, textarea { width: 295px; }

	.rules { display: none; }

	td.postblock { background: #375061 !important; border: 1px solid #2A4E68 !important; color: #486C96 !important; }

	td.postblock * { font-size: 6pt; }

 	td.reply, td.replyhl { background-color:#486C96 !important; border: 1px solid #2A4E68 !important; color: #46647A !important;-moz-border-radius: 8px; }

	td.replyhl { background-color:#4B4B4B !important; border: 1px solid #4B4B4B !important;	color: #46647A !important; }

	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr { color: #213645 !important; font-size: 8pt;}

	span.filetitle, .replytitle { color: #e15014 !important; }

	#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] { display: none !important; }

	.pages { background: #375061 !important; border: 1px solid #4B4B4B !important; }

	.pages td { color: #000 !important; font-size: 4pt; }

	.pages td a, .pages td b { color: #000 !important; font-size: 8pt; }

	.pages td b { background-color: #5A88A8; }

	.pages input { display: none !important; }

	.pages td:first-child { display: none;}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select { font-size: 9pt ! important; color: #213645 !important; background: #486C96 !important; border-color: gray !important; }

	input[type="submit"] { background: #375061 !important; color: #486C96 !important; border: 1px solid gray; -moz-appearance: none; font-size: 9pt; }

	th { background: #486C96 !important; }

	th font { font-size: 7pt; }

	input[type="file"] { opacity: 0.12 !important; }

	td.deletebuttons { font-size: 8pt !important; color:#486C96 !important; }

}
