css:
@-moz-document domain(bjork4um.com) {

body{background: rgb(61,1,29);
background: -moz-radial-gradient(circle, rgba(61,1,29,1) 0%, rgba(11,0,5,1) 100%);
background: -webkit-radial-gradient(circle, rgba(61,1,29,1) 0%, rgba(11,0,5,1) 100%);
background: radial-gradient(circle, rgba(61,1,29,1) 0%, rgba(11,0,5,1) 100%);
color: #f7f7f7;
background-attachment: fixed;
font-family: 'avus_proregular' !important;}

img {border-radius: 4px !important; max-width: 650px !important;}

a {color: #08819f !important}

h2, h3, a:hover {color: #b465b6 !important;}

ul.topiclist li {
    color: #fff;
}

.bullet, li.header dt, li.header dd {
    color: #08819f;
}


.username-coloured {
    color: #b465b6 !important;
}

li.row{
    border-radius: 4px;
}

li.row:hover {
    background-color: rgba(0,0,0,0.3) !important;
}

ul.topiclist dd,.postprofile {
    border-left-color: rgba(0,0,0,0.7) !important;
}

.navbar {
    color: #b465b6;
}

.forumbg {
    background-color: transparent !important;
}

.postbody, .postprofile strong{
    color: rgba(255,255,255,0.7);
}

.content {
    color: #f7f7f7;
}

.pagination ul li{
    display: inline-block !important;
    list-style-type: none !important;
}

.pagination ul {
    display: inline-block !important;
}

a.top {
    background-image: url(https://i.ibb.co/D4hQHLK/top.png);
}

.notice, fieldset.polls { font-family: 'avus_proregular' !important;}


.panel {
    margin-bottom: 6px;
    padding: 0 10px;
    background-color: #fff;
    border-radius: 4px;
  box-shadow: 0px 2px 4px rgba(0,0,0,0.7);
}


/*.postprofile {
    color: #666666;
    border-left-color: #FFFFFF;
}*/


blockquote, .uncited {
    background: transparent;
    border: solid #b465b6;
  border-width: 1px 1px 3px 1px;
  border-radius: 4px;
    font-size: 0.95em;
    margin: 0.5em 1px 12px 25px;
    overflow: hidden;
    padding: 15px;
}

.uncited {
    background: rgba(0,0,0,0.25);
}


.pagination span a, .pagination span a:link, .pagination span a:visited {
    color: #b465b6;
    background-color: #000;
  border-radius: 2px;
    border-color: transparent;
  
}


.report-icon, .report-icon a {
    background-image: url(https://i.ibb.co/GVw05fN/report.png);
}


ul.topiclist .header {
    font-size: 150%;
}

.panel .content {color: #1b1b1b !important}

.icon_post_target{padding-right: 4px !important}

.post:target .content {
    color: white !important; font-weight: bold;
}


.quote-icon, .quote-icon a {
    background-image: url(https://i.ibb.co/QYTmvKY/quote.png);
}

.pm-icon, .pm-icon a {
    background-image: url(https://i.ibb.co/Wy9N52d/pm.png);
}

input.tiny {
    width: 28px;
}


.post span.corners-top, .post span.corners-bottom, .panel span.corners-top, .panel span.corners-bottom, .navbar span.corners-top, .navbar span.corners-bottom {
    margin: 11px -10px;
}

li.dropdown-container.dropdown-button-control.dropdown-page-jump.page-jump {display:none !important;}


.edit-icon, .edit-icon a {
    background-image: url(https://i.ibb.co/V9CfmmL/edit.png);
}
.delete-icon, .delete-icon a {
    background-image: url(https://i.ibb.co/K6VZx2f/delete.png);
}

}
