@-moz-document domain("counter.social") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.uppercase
{
	text-transform: capitalize!important;
}

.bn-play::after,
.bn-arrow::after,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.slide-out-div.open
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.column-link__icon {
    margin-right: 26px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.column
{
	margin: 1px 12px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.drawer__inner {
    height: 99%!important;
}

}

@-moz-document domain("counter.social") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
.column {
    width: 23%!important;
}

.drawer {
    width: 13%!important;
}

.column:first-child,
.column:last-child {	
    width: 14%!important;
}

	.app-holder {
    height: 95%;
    margin-top: 9px;
}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

.column {
    width: 20%!important;
}
	
.drawer:first-child {
    width: 17%!important;
}

.column:last-child {	
    width: 17%!important;
}

	.app-holder,
	.killsmallscreen,
	.thermometer
	{
    height: 94%;
    margin-top: 9px;
}
	
	.getting-started__footer {
    margin: -17px!important;
}
	
}
}

@-moz-document domain("counter.social") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.notification__filter-bar button.active,
.notification__filter-bar button:hover,
.status__action-bar__counter__label,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.gray-tone,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.white,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.empty-column-indicator, .error-column
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.mb_YTVPBar
{
	color: #0000!important;
}


}

@-moz-document domain("counter.social") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.ui
{
	background: #060505!important;
	background-color: #060505!important;
}

.admin-wrapper .sidebar-wrapper,
.compose-form .compose-form__buttons-wrapper
{
    background-color: #101010!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.account__section-headline button,
.account__section-headline,
.admin-wrapper .sidebar ul ul,
.column-link,
.column>.scrollable,
.cososhare,
.dropdown-menu__item a,
.empty-column-indicator,
.error-column,
.flex-spacer,
.getting-started,
.getting-started__wrapper,
.notification__filter-bar button,
.notification__filter-bar,

[bordercolor="#282c37"]
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.button,
.home-button-1[href="https://counter.social/auth/sign_in"],
.simple_form button
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.button::before,
.home-button-1[href="https://counter.social/auth/sign_in"]::before,
.simple_form button::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.button:hover::before,
.home-button-1[href="https://counter.social/auth/sign_in"]:hover::before,
.simple_form button:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.home-button-1[href="https://counter.social/auth/sign_up"]
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.home-button-1[href="https://counter.social/auth/sign_up"]::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.home-button-1[href="https://counter.social/auth/sign_up"]:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#profile:not([class*='-active-leaf'])
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.column,
.drawer__inner,
.dropdown-menu,
.killsmallscreen
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #051af588 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

