.column:hover,
.dropdown-menu:hover,
.drawer__inner:hover,
.killsmallscreen:hover
{
	box-shadow: inset 0 0 5px #0006, #051af5 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.column-header
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.column-header
{
	border-top-left-radius: 22px!important;
	border-top-right-radius: 22px!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.admin-wrapper .sidebar ul a.selected,
.admin-wrapper .sidebar ul a:hover,

a.column-link:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.notification__filter-bar button.active
{
	background: radial-gradient(ellipse at center, #00809d 0%,#000 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("counter.social") {
/* Image Settings */

a.column-link,
article
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

a.column-link:hover,
article:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

