css:
@-moz-document domain("faz.net"),
domain("mycountrytalks.org") {
    body {
        margin: 0px auto !important;
        width: 100% !important;
        float: none !important;
        background: #d3d3d3 !important;
    }

    .gh-Wrapper {
        background-color: #d3d3d3 !important;
    }

    .gh-MainNav {
        background-color: #d3d3d3 !important;
    }

    .nvg-Breadcrumb_List:after {
        background-image: none !important;
    }

    .gh-Footer {
        background-color: #d3d3d3 !important;
    }

    .VWDcontent {
        background: none;
        background-color: #d3d3d3 !important;
    }

    .contentBox {
        background: none;
        background-color: #d3d3d3 !important;
    }

    #cna-P2 {
        background-color: #d3d3d3 !important;
    }

    .tsr-Top1_TeasersWrapper {
        background-color: #d3d3d3 !important;
        margin-right: 4px;
        margin-top: 20px;
        /*box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24), 0 17px 30px 0 rgba(0, 0, 0, 0.19);*/
        border: 1px solid lightgrey;
        padding-right: 10px;
    }

    .tsr-Top1_TeasersWrapper::before {
        background-color: #d3d3d3 !important;
    }

    .tsr-Top1_TeasersWrapper::after {
        background-color: #b5b4b4 !important;
        width: 100%;
    }

    .Artikel {
        background-color: #d3d3d3 !important;
    }

    .o-ModuleWrapper {
        background-color: #d3d3d3 !important;
    }

    .atc-Intro {
        background: none !important;
        margin-top: 30px;
        margin-bottom: 5px;
    }

    .ctn-PlaceholderBox {
        background-color: #d3d3d3 !important;
    }

    .cna-P4 {
        background-color: #d3d3d3 !important;
    }

    .tsr-Base {
        background-color: #d3d3d3 !important;
    }

    .ctn-PlaceholderContent {
        background-color: #d3d3d3 !important;
    }

    .tsr-Lead_TextWrapperOuter {
        background: none !important;
        border: none !important;
        margin: 20px 0 20px 0 !important;
        padding: 0 0 0 0 !important;
    }

    .tsr-MmBox.tsr-Base-has-border {
        border: none;
    }

    .mm-MmBox {
        background-color: #d3d3d3 !important;
    }

    .lbl-Base_Text {
        color: #494949;
    }

    .lbl-Base-has-bar.lbl-Base-is-inverted::after {
        background: #494949;
    }

    .lbl-Base-has-bar::after {
        background: #494949;
    }

    .tsr-Base-is-inverted .tsr-Base_Content,
    .tsr-Base-is-inverted .tsr-Base_ContentMeta,
    .tsr-Base-is-inverted .tsr-Base_ContentMetaItem,
    .tsr-Base-is-inverted .tsr-Base_HeadlineEmphasis,
    .tsr-Base-is-inverted .tsr-Base_HeadlineText {
        color: #494949;
    }

    .tsr-Base-is-inverted .tsr-Base_Content,
    .tsr-Base-is-inverted .tsr-Base_ContentMeta,
    .tsr-Base-is-inverted .tsr-Base_ContentMetaItem,
    .tsr-Base-is-inverted .tsr-Base_HeadlineEmphasis,
    .tsr-Base-is-inverted .tsr-Base_HeadlineText:hover {
        color: black;
    }

    .cna-P5 {
        background-color: #d3d3d3 !important;
    }

    .cna-P7 {
        background-color: #d3d3d3 !important;
    }

    .tsr-Base_MediaWrapper {
        border-radius: 4px;
        box-shadow: 0 8px 10px 0 rgba(0, 0, 0, 0.24), 0 10px 20px 0 rgba(0, 0, 0, 0.19);
        background: none !important;
        opacity: 0.7;
        border: #edefed ridge 1px;
    }

    .tsr-Base_MediaWrapper:hover {
        opacity: 1;
        border-radius: 4px;
        transition: 0.4s;
    }

    .tsr-Top1TextInPicture_MediaOverlay .tsr-Base_MediaWrapper {
        z-index: 0;
    }

    .atc-ImageContainer {
        border-radius: 4px;
        box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24), 0 17px 50px 0 rgba(0, 0, 0, 0.19);
        background: none !important;
        opacity: 0.7;
        border: #edefed ridge 1px;
    }

    .atc-ImageContainer:hover {
        opacity: 1;
        border-radius: 4px;
        transition: 0.4s;
    }

    .atc-Text {
        margin-top: 4px;
        margin-bottom: 10px;
    }

    .atc-ContainerPaywall::before {
        background: none !important;
    }

    .ctn-PaywallInfo {
        background: none !important;
        border: red 1px solid;
    }

    aco-ContentPayment .aco-ContentPayment_Panel,
    .aco-ContentPayment .ui-accordion-header.aco-ContentPayment_Panel,
    .aco-ContentPayment .ui-state-active.aco-ContentPayment_Panel,
    .aco-ContentPayment .ui-state-default.aco-ContentPayment_Panel,
    .aco-ContentPayment_Panel {
        background-color: lightgrey !important;
    }

    .aco-ContentPayment_Content,
    .aco-ContentPayment_Content.ui-accordion-content.ui-widget-content {
        background-color: lightgrey !important;
    }

    .ctn-PaywallTeasers {
        background-color: #d3d3d3 !important;
    }

    .gh-MainNav_Article::after {
        background-image: none !important;
    }

    .ctn-PlaceholderContent_Description-has-text-overline {
        background-color: #d3d3d3 !important;
    }

    .ctn-PlaceholderContent_Description {
        background-color: #d3d3d3 !important;
    }

    .atc-IntroText {
        text-align: justify;
    }

    .atc-TextParagraph {
        text-align: justify;
    }

    .tsr-Base_Content {
        text-align: justify;
    }

    .cna-P4a {
        background: none !important;
    }
    #mainpage-snacks {
        display: none;
    }

    .tsr-LeadWide_TextWrapperOuter {
        background-color: #d3d3d3 !important;
        margin-right: 4px;
        margin-top: 20px;
        padding-right: 10px;
    }

    .ctn-Topic {
        border-radius: 4px;
    }

    .ctn-Topic_Headline {
        border-radius: 4px !important;
    }

    .o-Grid {
        padding-top: 5px;
    }

    .atc-ContainerPaywall {
        background: none !important;
        padding: 5px;
    }
    .cmt-Overlay_Main {
        background-color: #d3d3d3 !important;
    }
    .talk-plugin-infobox-info {
        background-color: #d3d3d3 !important;
    }
    .talk-stream-comment {
        background-color: #d3d3d3 !important;
        border-radius: 4px !important;
    }
    .talk-stream-comment-container {
        background-color: #edefed !important;
        padding: 10px;
        border-radius: 4px !important;
    }
    .talk-stream-comment-wrapper {
        background-color: #d3d3d3 !important;
    }
    .talk-tab-button {
        background-color: #d3d3d3 !important;
        border: #edefed groove 1px !important;
    }
    .talk-stream-auth-sign-in-button button {
        border-radius: 4px;
    }
    .gf-Wrapper {
        background-color: #d3d3d3 !important;
    }
    .gf-Wrapper_Inner {
        background-color: #edefed !important;
    }
    .gh-Ressort_SecondLevel {
        background-color: #edefed !important;
    }
    .mod_socialShare_link {
        background-color: #edefed !important;
        color: #c60000;
        border: 1px lightgrey groove;
    }
    .mod_socialShare_item {
        border-radius: 4px !important;
    }
    .marktueberblickHP {
        background-color: #d3d3d3 !important;
    }
    .VWDcomp .marktueberblickHP ul.list_charts li a {
        border: 1px #edefed solid;
        border-radius: 4px;
    }
    .gh-Ressort_OverlayContainer::before {
        opacity: 0.5 !important;
    }
    .gh-Ressort_OverlayContainer::after {
        opacity: 0.5 !important;
    }
    .suchform {
        background-color: #d3d3d3 !important;
    }

    #PBAd {
        background-color: #d3d3d3 !important;
    }

    #PB2Ad {
        background-color: #d3d3d3 !important;
    }
    #cna-P4a {
        background-color: #d3d3d3 !important;
        border-bottom: 1px #c60000 solid;
    }

    #cna-P5a {
        background-color: #d3d3d3 !important;
        border-bottom: 1px #c60000 solid;
    }

    #cna-N1 {
        background-color: #d3d3d3 !important;
        border-bottom: 1px #c60000 solid;
    }

    #cna-P5b {
        background-color: #d3d3d3 !important;
        border-bottom: 1px #c60000 solid;
    }

    .player {
        background-color: #d3d3d3 !important;
        border-radius: 4px;
        border: 1px darkgrey groove !important;
    }

    .override-body-color {
        background-color: #d3d3d3 !important;
    }

    .override-background-color {
        background-color: #d3d3d3 !important;
    }

    .embed {
        background-color: #d3d3d3 !important;
    }


    .embed__inner {
        border: 1px #c60000 solid !important;
    }

    .mm-adbox {
        background-color: #d3d3d3 !important;
    }
    #FAZContent {
        background-color: #d3d3d3 !important;
    }

    .VWDcomp {
        background-color: #d3d3d3 !important;
        border: 1px #c60000 solid !important;
        border-radius: 4px;
        padding: 3px;
    }

    .ContentFooter {
        background-color: #d3d3d3 !important;
    }
    .VWDcontent * {
        background-color: #d3d3d3 !important;
    }

    .container-fluid {
        background-color: #d3d3d3 !important;
    }
    #page_mobile_header {
        background-color: #d3d3d3 !important;
    }
    .header {
        background-color: #d3d3d3 !important;
    }
    #ressortsubmenu {
        background-color: #d3d3d3 !important;
    }
    .faz__spacer {
        background-color: #d3d3d3 !important;
    }
    .o-Grid {
        background-color: #d3d3d3 !important;
    }
    .tsr-Top1Portrait_TextContainer {
        background-color: #d3d3d3 !important;
    }
    .sld-TeaserCommentColumnWithButton-has-background {
        background-color: #d3d3d3 !important;
    }
    .ctn-Topic-green{
        display: none;
    }
.ctn-Sharing_ContentWrapper {
        background-color: #d3d3d3 !important;
        box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24), 0 17px 50px 0 rgba(0, 0, 0, 0.19);
    }
}
