/* ==UserStyle==
@name           KanbanFlow - Dark Mode
@namespace      USO Archive
@author         Brian Wigginton
@description    `A simple and dark mode for kanban flow.`
@version        1
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain(kanbanflow.com) {
    div.taskList {
        background-color: #222;
        border-color: #6b636b;
        border-style: none;
    }

    div.boardHeader {
        background-color: #404040;
    }

    h1.boardHeader-boardName.truncate {
        color: #ffffff;
    }
    
    .boardHeader-shortcut,
    .boardHeader-menuButton .icons-menu {
        filter: invert(1);
    }

    div.columnHeader-inner {
        background-color: #1c1c1c;
    }

    h2.columnHeader-name {
        color: #ffffff;
    }

    i.columnHeader-descriptionIcon.icons-info {
        color: #ffffff;
        border-color: #000;
        background-color: #ffffff;
    }

    button.icons-timer.boardHeader-timerShortcut.boardHeader-shortcut.icons--button {
        color: #ffffff;
    }

    div.board-wrapper-inner {
        background-color: #1f1f1f;
    }

    div.board-collapsedColumnName {
        background-color: #141414;
    }

    h2.collapsed-info-title, .columnHeader-count {
        color: #e0e0e0;
    }

    .board-taskListCell {
        border-color: #000;
    }

    .column-first {
        border-color: #000;
    }

    .columnHeader {
        border-color: #000;
    }

    span.boardHeader-menuName {
        color: #ffffff;
    }

    i.icons-menu {
        color: #ffffff;
    }

    .task {
        color: #cacaca;
    }
    
    .task-groupLabel {
        color: #aaa;
        text-align: center
    }

    #header {
        background-color: #000;
    }

    div.boardHeader {
        background-color: #111;
    }

    .task.task-red {
        background-color: #5d1012;
        border: 1px solid #922323;
    }

    .task.task-orange {
        background-color: #682d10;
        border: 1px solid #7f3913;
    }

    .task.task-yellow {
        background-color: #5e4500;
        border: 1px solid #6c4400;
    }

    .task.task-green {
        background-color: #1b4721;
        border: 1px solid #245829;
    }

    .task.task-blue {
        background-color: #102d5c;
        border: 1px solid #1b4a91;
    }

    .task.task-cyan {
        background-color: #003e3e;
        border: 1px solid #006262;
    }

    .task.task-magenta {
        background-color: #551639;
        border: 1px solid #7e325a;
    }

    .task.task-purple {
        background-color: #362160;
        border: 1px solid #5936a2;
    }


}
