css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://),
url-prefix(https://),
url-prefix(about:blank),
url-prefix(ftp://),
url-prefix(gopher://) {

/* BG colors */

* {
    background-color: #e6e6e6 !important;
}

input[type=password], input[type=password] *,
input[type=text], input[type=text] *,
textarea, textarea * {
    background-color: #f4f4f4 !important;
}

p:not(:empty), p *,
h1, h1 *, h2, h2 *, h3, h3 *, h4, h4 *, h5, h5 *,
strong>*, b>*, em>*, i>*, span>*:not(img) {
    background: transparent none !important;
}

/* FG colors */

* {
    color: #222 !important;
    border-color: #999999 !important;
}

h1, h1 * {
    color: #444 !important;
}
h2, h2 * {
    color: #333 !important;
}

*[onclick],
a:not([name]), a:not([name]) *  {
    color:#0000aa !important;
}

*[onclick]:hover,
a:not([name]):visited, a:not([name]):visited * {
    color: #7575aa !important;
}

a:not([name]):hover, a:not([name]):hover * {
    color: #000075 !important;
    text-decoration: underline !important;
}

p>strong:only-of-type, p>b:only-of-type {
    color: #505050 !important;
}

pre:not(:empty), code:not(:empty), cite:not(:empty),
pre:not(:empty) *, code:not(:empty) *, cite:not(:empty) * {
    color: #000050 !important;
}

hr {
    background: #999999 none !important;
    color: #999999 !important;
    border: 1px solid #999999 !important;
    height: 1px !important;
    overflow: hidden !important;
    display: block !important;
}


*:not([onclick]):not(input):not(a):not(img):not([class^="UI"]),
a:not(:empty),
div:not([onclick]) {
    background-image: none !important;
    text-indent: 0 !important;
}
}
