css:
@-moz-document domain(steamcommunity.com) {
  .achievementProgressBar.ellipsis {
		overflow: visible;
        min-width: 300px;
  }

  .progressText.inBar.ellipsis,
  .progressText.nextToBar.ellipsis {
    	font-size: 11px;
  }
  
  .progressText.nextToBar.ellipsis {
    	margin: -0.5px 0 0;
  }
}

@-moz-document domain(youtube.com) {
  .ytd-watch-flexy[theater]#player-theater-container.ytd-watch-flexy, ytd-watch-flexy[fullscreen]#player-theater-container.ytd-watch-flexy {
    height: calc(100vh - 56px);
  }
}
