@-moz-document domain("rust-lang.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

pre > .buttons button,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.hljs
{
	font-size: 2.5rem!important;
}

body,
code,
p
{
	font-size: 1.85rem!important;
}

code
{
	line-height: 2.5rem!important;
}

.hljs
{
	line-height: 3.5rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

p code
{
	padding: 3px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.content main
{
	max-width: none!important;
}

.content main {
    width: 92%!important;
}

}

@-moz-document domain("rust-lang.org") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("rust-lang.org") {
/* Font Settings */

#searchresults a, .content a:link, a:visited, a > .hljs,
a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#searchresults a:hover,

.ace_gutter-cell:hover,
.content a:link:hover,

a > .hljs:hover,
a:visited,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
p code
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

:not(pre):not(a) > .hljs,
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/


/*31f1ff*/
.ace-tomorrow-night .ace_keyword,
.ace-tomorrow-night .ace_meta,
.ace-tomorrow-night .ace_storage,
.ace-tomorrow-night .ace_storage.ace_type,
.ace-tomorrow-night .ace_support.ace_type {
  color: #31f1ff !important;
}
/*8cff9a*/
.ace-tomorrow-night .ace_heading,
.ace-tomorrow-night .ace_markup.ace_heading,
.ace-tomorrow-night .ace_string {
  color: #8cff9a;
}
/*bcbcbc*/
.ace-tomorrow-night .ace_comment {
  color: #bcbcbc !important;
}
/*ff5090*/
.ace-tomorrow-night .ace_entity.ace_name.ace_function,
.ace-tomorrow-night .ace_support.ace_function,
.ace-tomorrow-night .ace_variable {
  color: #ff5090 !important;
}
/*ff7312*/
.ace-tomorrow-night .ace_constant.ace_character,
.ace-tomorrow-night .ace_constant.ace_language {
  color: #ff7312 !important;
}
/*ff9*/
.ace_text-layer > .ace_line {
  color: #ff9 !important;
}
/*fff*/
.ace-tomorrow-night .ace_heading,
.ace-tomorrow-night .ace_markup.ace_heading,
.ace-tomorrow-night .ace_string,
.ace_punctuation.ace_operator,
.ace-tomorrow-night .ace_keyword.ace_operator {
  color: #fff !important;
}
/*fffd00*/
.ace-tomorrow-night .ace_constant.ace_numeric,
.ace-tomorrow-night .ace_keyword.ace_other.ace_unit,
.ace-tomorrow-night .ace_support.ace_constant,
.ace-tomorrow-night .ace_variable.ace_parameter {
  color: #fffd00 !important;
}






}

@-moz-document domain("rust-lang.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.sidebar
{
    background-color: #101010!important;
}

.hljs
{
	background: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

pre > .buttons button
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/


/* Red Gradient 
_____________________________________________________________________________________________________*/

.nav-chapters.previous:hover
{
	background: linear-gradient(to right, #590505 0%, #000 100%)!important;
}

.nav-chapters.next:hover
{
	background: linear-gradient(to left, #590505 0%, #000 100%)!important;
}

/* Code 
_____________________________________________________________________________________________________*/

#content p code,
pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.ace_line:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

}

@-moz-document domain("rust-lang.org") {
/* Image Settings */

.content ul li,

button
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.content ul li:hover,

button:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("rust-lang.org") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.fa {
    font-size: 38px!important;
}

/* Arrows _______________________________________________________________________*/

.fa-angle-right:before,
.fa-angle-left:before {
    content: "";
}

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

.fa-angle-left:before
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.fa-angle-right:before
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-angle-right:before,
.fa-angle-left:before
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Copy _________________________________________________________________________*/

.fa-copy:before {
    content: "";
}

.fa-copy:before
{
	background: url(https://i.ibb.co/W6nfftb/copy2.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-copy:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* GitHub _____________*/

.fa-github:before {
    content: "";
}

.fa-github:before {
	background: url(https://i.ibb.co/pK89B3j/github-30x30.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-github:before {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Paint Brush ___________________________________________________________________________*/

.fa-paint-brush:before {
    content: "";
}

.fa-paint-brush:before
{
	background: url(https://i.ibb.co/QQswXjS/painters-pallett.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-paint-brush:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important
}

/* Play ______________________________________________________________________________*/

.fa-play:before {
    content: "";
}

.fa-play:before
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-play:hover:before
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-play:hover:before,
.fa-play:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important
}

/* Print _______________________________________________________________________________*/

.fa-print:before {
    content: "";
}

.fa-print:before
{
	background: url(https://i.ibb.co/gST2svf/print.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-print:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important
}

/* Search ___________________________________________________________________________*/

.fa-search:before {
    content: "";
}

.fa-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important
}

/* Undo_____________________________________________________________________________*/

.fa-history:before {
    content: "";
}

.fa-history:before
{
	background: url(https://i.ibb.co/4pFKSYL/undo.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-history:before
{
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 1px!important
}


}

@-moz-document domain("rust-lang.org") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
