css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://dragcave.net") {

body {
	background: #000000 url("http://www.fireflyforest.com/iridias_stuff/images/back/stones1/cavsoftm.jpg")!important;
	font-size: 12pt !important;
	font-family: eurofurence !important;
}

#top {
	background: url("http://imgur.com/NF6Mj.png") no-repeat !important;
	width: 825px !important;
	color: #000 !important;
	height: 123px !important;
}
#middle {
	background: none repeat-y !important;
	padding: 0 60px !important;
	color: #000 !important;
	min-height: 100px !important;
	width: 705px !important;

}
#bottom {
	background: url("http://imgur.com/ulW1s.png") no-repeat !important;
	color: #000 !important;
	width: 825px !important;
	height: 110px !important;
}

b {     
	color:#00C000 !important;font:11px georgia !important;
	font-style:italic !important;
	font-weight:bold !important;letter-spacing:-.5pt !important;
}

a:link, a:active, a:visited {
	color:#FF40FF !important;
	font:12px eurofurence !important;
	font-style:italic !important;
}
a:hover {
	color:#000000 !important;
}

.csort2 img{
	background: #FFC0FF !important;
}

td, tr, th img{
	background: !important;
}

TEXTAREA {
	height: 17px !important;
	color:#C0C0C0 !important;
	font:8pt Trebuchet MS !important;
	text-transform: inherit !important;
}

.cave,.scroll,.login,.register,.shop,.forum,.logout {
	margin: 0 5px !important;
	background: transparent url("http://imgur.com/aCJcW.png") no-repeat !important;
	height: 50px !important;

}
.cave {
	width: 88px !important;
}
.scroll {
	width: 91px !important;
	background-position: -90px center !important;
}
.login {
	width: 95px !important;
	background-position: -177px center !important;
}
.register {
	width: 138px !important;
	background-position: -272px center !important;
}
.shop {
	width: 82px !important;
	background-position: -362px center !important;
	display:none !important;
}
.forum {
	width: 115px !important;
	background-position: -412px center !important;
}
.logout {
	width: 123px !important;
	background-position: -526px center !important;
}

table, td, td{
	text-align: center !important;
	font-weight: normal !important;
	font-size: 13px !important;
	border-spacing: 5px !important;
}

INPUT {
	color:#333 !important;
	font:8pt Trebuchet MS !important;
	text-transform: lowercase !important;
}

html,body {
	padding: 0 !important;
	margin: 0 !important;
}


p {
	text-indent: 1.5em !important;
}


h1,h2 {
	padding: 0 !important;
	margin: 0 !important;
}


th,td {
	text-align: center !important;
	vertical-align: top !important;
}
table.vamid td {
	vertical-align: middle !important;
}
table.leftalign,table.leftalign td,table.leftalign th {
	text-align: left !important;
}
img {
	border-width: 0 !important;
}
acronym {
	cursor: help !important;
}
#wrapper {
	width: 900px !important;
	max-width: 800px !important;
	margin: auto !important;
	text-align: justify !important;
}

#logo {
	display: block !important;
	margin: 0 auto !important;
	width: 562px !important;
	height: 127px !important;
	display:none !important;

}
#menu {
	text-align: center !important;
	margin: 0 auto !important;
}

#prefooter {
	margin-left: -11.5px !important;
	height: 100px !important;
	overflow: hidden !important;
	width: 728px !important;
	margin-right: -20px;
}
#footer {
	font-size: 0.8em !important;
	text-align: center !important;
	margin: 0 auto;

}
}
