css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sddp.sirsi.net") {

/* 
Colors:
light blue:  #E0EBEF;
medium blue: #0099CC;
dark blue:   #006699;
*/


/* Font */
body,div,td,a,p,input,select,textarea,label,
.viewmarctags,.tab, .enrichtagline {
  font-family: Verdana !important;
  font-size: 8pt !important;
  letter-spacing: normal !important;
}


/* Emphasis */
em {
  font-weight: bold !important;
  font-style: normal !important;
}


/* Links */
a {
  color: #006699 !important;
}


a:hover {
  color: #006699 !important;
  text-decoration: underline !important;
  background-color: inherit !important;
}


a.holdicon {
  padding: 3px !important;
  color: white !important;
  font-weight: bold !important;
}


a.holdicon:hover {
  color: white !important;
  background-color: #0099CC !important;
  text-decoration: none !important;
}


label {
  white-space: nowrap !important;
}



/* Buttons (IE) */
input {
  font-weight: expression( ((( this.type=="button" | this.type=="submit") | this.type=="cancel") | this.type=="reset") ? bold : normal);
}

input:hover {
  cursor: expression( ((( this.type=="button" | this.type=="submit") | this.type=="cancel") | this.type=="reset") ? hand : normal);
}

/* Buttons (Firefox, others) */
input[type="button"],input[type="submit"],input[type="cancel"],input[type="reset"] {
  font-weight: bold !important;
}

input[type="button"],input[type="submit"],input[type="cancel"],input[type="reset"]:hover {
  cursor: pointer !important;
}


/* Classes */
.itemlisting {
  white-space: normal !important;
  width: auto !important;
}


.itemlisting2 {
  background-color: #E0EBEF !important;
  white-space: normal !important;
  width: auto !important;
}


.header {
  padding: 6px !important;
  font-size: 11pt !important;
}


.tab {
  margin: 0px !important;
  padding-top: 2px !important;
  padding-left: 10px !important;
  padding-right: 10px !important;
}

/* Tables and Cells */

table {
  border-spacing: 0px !important;
  border-collapse: collapse !important;
  empty-cells: hide;
}


td, th {
  padding: 4px !important;
  white-space: nowrap !important;
}


td.titlebar {
  padding-right: 20px !important;
  font-size: 15pt !important;
  white-space: nowrap !important;
}


.enrichheader {
  font-size: 10pt !important;
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  background-color: white !important;
}


td.enrichcontent {
  font-style: normal !important;
  text-align: left !important;
  background-color: white !important;
}


table.gatewaystyle {
  width: 100% !important;
  text-align: right !important;
  margin-bottom: 0px !important;
  vertical-align: bottom !important;
  margin-bottom: 8px !important;
}


td.rootbarcell {
  white-space: normal !important;
  padding-left: 140px !important;
  padding-right: 140px !important;
  padding-top: 5px !important;
  padding-bottom: 5px !important;
  line-height: 20px !important;
}


td.rootbarcell a {
  font-weight: bold !important;
  color: #CFECEC !important;
}


table.defaultstyle {
  border: 1px solid #006699 !important;
  margin: 0px !important;
}


td.defaultstyle {
  padding: 4px !important;
  margin: 0px !important;
  text-align: left !important;
  white-space: normal !important;
}


td.indented {
  text-align: left !important;
  padding-left: 5px !important;
  padding-bottom: 12px !important;
}


th.searchcontent {
  width: 10px !important;
  background-color: #E0EBEF !important;
  text-align: left !important;
}


td.searchcontent {
  width: 100% !important;
  background-color: #E0EBEF !important;
  text-align: left !important;
  empty-cell: show !important;
}


p.searchsum {
  text-align: left !important;
  padding-left: 5px !important;
}




th.viewmarctags {
  white-space: nowrap !important;
  text-align: left !important;
  width: 0px !important;
}

td.viewmarctags {
  white-space: normal !important;
  width: 100% !important;
}


/* Divs */

div.pagecontainer, div.pagecontainer3pg {
  width: 1000px !important;
  margin-left: auto !important;
  margin-right: auto !important;
  border: 1px solid #006699 !important;
}


div.searchservices {
  background-color: #E0EBEF !important;
  width: 550px !important;
  margin-bottom: 30px !important;
  margin-left: 12px !important;
}


div.searchheader {
  padding: 4px !important;
  font-size: 11pt !important;
  display: table-cell !important;
  width: 542px !important;
}


div.itemservices {
  margin-left: 8px !important;
  margin-right: 8px !important;
  border: 1px solid #006699 !important;
}


div.panel, div.panelholder, div.bibinfo, 
div.bibinfo2, div.panel, div.summary {
  margin: 0px !important;
  width: 100% !important;
  border: none !important;
  white-space: normal !important;
}


div.panel {
  border: 1px solid #006699 !important;
}

div.bibinfo {
  white-space: nowrap !important;
}

.panel table {
  margin-top: 8px !important;
  width: 100% !important;
}


.panel th {
  border-top: 1px solid #006699 !important;
  border-bottom: 1px solid #006699 !important;
  border-left: 1px solid #006699 !important;
}


.panel td {
  border: 1px solid #006699 !important;
}



/* Login */
#user_id, #password {
  width: 10em !important;
}


/* Hide */
.subheader, .footer {
  display: none !important;
}


}
