css:
/* i really want this to be global */
/* Box header */
.splash-header .news .box-header{
 display:none;
}

/* List Item */
.news ul li{
 display:none;
}

/* News */
.splash .mod-splash .news{
 display:none;
}

/* Splash */
#view .splash{
 transform:translatex(0px) translatey(0px);
}

/* Ignore react onclickoutside */
#navigation ul .ignore-react-onclickoutside{
 transform:translatex(0px) translatey(0px);
}

/* Page */
#app .page{
 filter: grayscale(1);
}
