/* ==UserStyle==
@name           AniList Slim Home
@namespace      https://anilist.co/user/hexd
@version        1.0.0
@description    Narrower AniList home page to make text posts easier to read
@author         ꝇaꭵꝴ
@supportURL	    https://anilist.co/forum/thread/61037
==/UserStyle== */

@-moz-document url-prefix("https://anilist.co/home") {
    /*feed*/
    .activity-feed-wrap {
        max-width: 570px;
        margin-left: auto;
        /* margin-right: 80px; */
    }
    
    /*text post padding*/
    .text[data-v-04d66030] {
        padding: 28px;
    }

    /*previews*/
    .list-preview,
    .list-previews,
    .recent-threads,
    .recent-reviews,
    [data-v-796abd11] {
        max-width: 440px;
        /*margin-right: 100px;*/
        margin-left: 0px;
    }


    /*list cards*/
    .list-preview[data-v-6aea970d] {
        grid-gap: 0px;
        padding: 10px 6px 6px 6px;
    }

    /*trending cards*/
    .media-preview[data-v-796abd11] {
        grid-gap: 3px;
        padding: 7px;
        grid-template-columns: repeat(auto-fill, 80px);
    }

    /*small media covers*/
    /*.small .cover[data-v-6dc78144],
    .media-preview-card.hohFallback,
    .media-preview-card,*/
    .list-preview .cover,
    .media-preview .cover{
        scale: .8;
    }


    /*right grid gap*/
    .home[data-v-6860dbfd] {
        grid-column-gap: 40px;
        grid-template-columns: auto 640px;
    }
    
    /*hoh feed filter*/
    .hohFeedFilter {
        left:-150px;
    }
    
    /***********
    LIST UPDATES
    ************/
    .activity-entry{
        font-size:1.1rem;
    }
    
    .home .activity-feed {
        /*grid-template-columns: repeat(2,1fr);*/
        grid-column-gap: 15px;
        display: block;
        
    }
}
