css:
@-moz-document domain("rocket-league.com") {
.rlg-ven-preloader{
    display:none;
}
.rlg-hero-banner{
    position: relative;
    height: 200px;

}
.rlg-header-main-menu {
    padding: 7px 0 0;
}
.rlg-header-main-menu .rlg-wrapper {
    height: 91px;
    position: relative;
    display: -ms-flexbox;
    display: flex;
}
.rlg-header-main-menu #rlg-logo {
    width: 65px;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
}
.rlg-header-main .rlg-header-main-bar {
    font-size: 13px;
    font-size: .8125rem;
    font-family: rajdhani-medium-webfont,Helvetica,sans-serif;
    font-style: normal;
    border-bottom: 1px solid #1e1f26;
    background: #22242c;
    color: #fff;
    height: 38px;
    letter-spacing: 2px;
    text-transform: uppercase;
    width: 100%;
}
}
