css:
@-moz-document domain('youtube.com')
{
    /* Likes */
  #actions-inner > :nth-child(1) > :nth-child(1) > :nth-child(1) > :nth-child(1) *
  {
    background-color: rgba(0,0,0,0);
  }

  /*Download, clip*/
  #actions-inner > :nth-child(1) > :nth-child(1) > :nth-child(2) *
  {
    background-color: rgba(0,0,0,0);
  }

  /*Share*/
  #actions-inner > :nth-child(1) > :nth-child(1) > :nth-child(1) > :nth-child(2) *
  {
    background-color: rgba(0,0,0,0);
  }

  /*Dots menu*/
  #actions-inner > :nth-child(1) > :nth-child(1) > :nth-child(4) *
  {
    background-color:rgba(0,0,0,0);
  }

  /*Round boxes*/
  #description,
  ytd-rich-metadata-renderer
  {
    background-color: rgba(0,0,0,0) !important;
  }

  /*Hide blink when unfolding description */
  #description-interaction
  {
    display: none;
  }
}
