css:
@-moz-document domain(badoo.com) {
.body, .messenger-ovl__content, .messenger-tools__form, .page__content, .user-card__photo, .page__content-footer, .encounters-card, .sidebar--auth, .profile__main-info, .profile__main-surprise, .profile-header, .profile__additional-info, .text-field__input, .fast-message__text {
  background: #121212 !important;
  color: whitesmoke;
}
.messenger-tools__field, .chat-bubble-simple--in, .user-card__actions, .messenger-tools__controls, .ovl__frame, .dropdown, .contact-banner:hover, .contact-card:hover, .contact-card.is-current, .select-field__label, .fast-message::before, .fast-message__inner, .dropdown::before, .pill, .messenger__banner {
  background: #242424;
  color: whitesmoke;
}
.option:hover {
  background: #363636;
}
.btn--monochrome, .contact-banner__text, .sidebar-menu__item, .contact-card__name, .contact-card__message, .profile-header__user, .select-field__icon, .select-field__label-text, .connection-header__user--link, .user-card__action, .messenger-tools__icon, .sidebar-info__name, .sidebar-info__metric-content, .sidebar-info__metric-value, .spotlight__title, .sidebar-popularity__title, .sidebar-menu__filter-icon, .sidebar-menu__item, .cta-box__media, .option, .profile-section__title-text, .dialog__note, .text-color-black, .personal-info__label, .messenger-banner__title, .messenger-banner__desc {
  color: whitesmoke;
}
.btn--monochrome {
  border-color: whitesmoke;
}
.select-field__label, .user-card__actions {
  border: none;
}
.user-card__action:not(.is-animated)+.user-card__action {
  border-color: whitesmoke;
}
.page__content-footer, .messenger__header, .messenger__side, .sidebar__section--info, .profile__section, .profile__additional-info::after, .messenger__banner {
  border-color: #363636 !important;
}
.ovl {
  background: rgba(0,0,0,0.5);
	backdrop-filter: blur(5px);
}
.st_msgs_disable .scroll[data-scroll-root=messenger__content]::after, .st_msgs_disable--with-gifts .scroll[data-scroll-root=messenger__content]::after, .st_normal .scroll[data-scroll-root=messenger__content]::after {
  background-image: linear-gradient(to bottom,rgba(0, 0, 0,0) 0%,rgba(0, 0, 0,.069) 12.3%,rgba(0, 0, 0,.152) 22.7%,rgba(0, 0, 0,.247) 31.6%,rgba(0, 0, 0,.352) 39.7%,rgba(0, 0, 0,.463) 47.4%,rgba(0, 0, 0,.576) 55.3%,rgba(0, 0, 0,.69) 64%,rgba(0, 0, 0,.8) 74%,rgba(0, 0, 0,.904) 85.8%,#000 100%);
}
}
