css:
@-moz-document domain("community.openstreetmap.org")
{

.discourse-reactions-actions {display:none}
.topic-navigation {display:none}
.category-logo {display:none}
table > tbody > tr > td.category {border:none !important}
.badge-category-bg {display:none !important}
.like-count, .badges, .badge-info, .badge-contents, .favorite-count, .badge-group-list {display:none !important}
.trust-level {display:none !important}
.nav-pills>li a.active {background-color:grey !important}
.nav-pills>li>a:hover {color:white; background-color:lightgray !important}
.likes, .topic-list .posters, .topic-avatar, .post-avatar {display:none !important}
div > div.topic-body.clearfix > div.regular.contents > div > aside > div > img {display:none !important}
div > div.topic-body.clearfix > div.regular.contents > section.embedded-posts.bottom > div > div > div > div.topic-body > div.cooked > aside > div > img {display:none !important}
 .small-action.topic-post-visited .topic-post-visited-line .topic-post-visited-message {color:black !important}
.small-action.topic-post-visited .topic-post-visited-line {border-bottom:1px solid #000;}

}
