css:
@-moz-document domain(voiranime.com) {
  body, .profile-manga {
    color: white !important;
    background: #333 !important;
  }

  .url, .h5 a, .h4, .wp-manga-chapter a, .post-title h1, .post-content_item div h5, .comment-content p {
    color: white !important;
  }

  .ion-checkmark, .c-selectpicker.selectpicker_chapter.selectpicker.single-chapter-select, .selectpicker.host-select {
    filter: invert() !important;
  }

  .summary_image, .summary_content, #comment, #author, #email, #url {
    border-radius: 5px;
    border: #444 solid 2px;
    background: #555 !important;
  }

  #comment:hover, #author:hover, #email:hover, #url:hover {
      border-radius: 5px;
    border: #555 solid 2px;
    background: #666 !important;
  }
}
