css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("7chan.org"),
url("https://www.7chan.org/b/") {



html, body {
background-image:url(http://gickr.com/results4/anim_45c91ff5-e49a-f424-4d99-688b4e146cb0.gif) !important; 
background-repeat:repeat; 
background-attachment:fixed; 
background-color: black !important;
color: #FFFFFF !important;
font-family: "Trebuchet MS",Trebuchet,serif;
}
a {
color: #00FFFF !important;
}
a:hover {
color: #00FFFF;
}

blockquote{
color: #FFFFFF !important;
}

.adminbar {
clear:both;
float:right;
font-size: .8em;
}
.adminbar a {
font-weight: bold;
}
.logo {
clear:both;
text-align:center;
font-size:2em;
font-weight: bold;
color:#0066FF !important;
width:100%;
}
.theader, .passvalid {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important; 
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
text-align:center;
padding:2px;
color:#FFFFFF;
clear: both;
font-weight: bold;
margin-bottom: .5em;
border: solid 1px #CCCCCC;
-moz-border-radius: 5px;
}
.postarea {
}
.rules {
font-size:0.7em;
}
.postblock {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important; 
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
color:#FFFFFF;
font-weight:bold;
}
.footer {
text-align:center;
font-size:12px;
font-family:serif;
margin: 2em 0 0 0;
}
.dellist {
font-weight: bold;
text-align:center;
}
.delbuttons {
text-align:center;
padding-bottom:4px;
}
.managehead {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important; 
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
color:#FFF;
padding:0px;
}
.postlists {
background:#FFFFFF;
width:100%;
padding:0px;
color:#FFF;
}
.row1 {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important; 
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
color:#FFF;
}
.row2 {
background:#CCCCCC;
color:#FFF;
}
.unkfunc {
background:inherit;
color:#789922;
}
.reflink {
font-size: .8em;
font-weight: bold;
}
.filesize {
text-decoration:none;
color: #FFF;
font-size: .8em;
}
.filetitle {
background:inherit;
font-size:1.2em;
color:#FFF;
font-weight:bold;
}
.postername {
color:#004A99;
font-weight:bold;
}
.postertrip {
color:#FF3300;
}
.oldpost {
color:#CC1105;
font-weight:bold;
}
.omittedposts {
color:#FFF;
}
.reply {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important; 
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
}
blockquote {
margin: .5em .5em .5em 1em;
}
blockquote p {
margin: 0;
padding: 0;
}
.reply blockquote {
margin: .5em;
}
.doubledash {
display: none;
 vertical-align:top;
clear:both;
float:left;
}
.replytitle {
font-size: 1.2em;
color:#FFF;
font-weight:bold;
}
.commentpostername {
color:#004A99;
font-weight:800;
}
.thumbnailmsg {
font-size: .8em;
color:#FFF;
}
hr {
border-style: solid none none none;
border-width: 1px;
border-color: #BBBBBB;
}
table {
border-style: none;
}
table td {
border-style: none;
}
.nothumb {
background-color: #FFFFFF;
border-style: dotted;
margin: .3em .5em;
}

.abbrev {
color:#666666;
}
.highlight {
background:#EEDACB;
color:#FFF;
border: 2px dashed #EE6600;
}

.watchedthreads {
background-image:url(http://gickr.com/results4/anim_55d5f1b4-6200-9b84-bd5b-9cab921792cc.gif) !important;
background-position:top left; 
background-repeat:repeat; 
background-attachment:fixed; 
  border: 1px solid #CCCCCC !important;
  -moz-border-radius: 5px;
}
}
