@-moz-document domain("videoshub.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

selector,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.font-size-12 i {
    font-size: 1.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.custom_pagination .page-item:not(:last-child) .page-link
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container,
.container-fluid,
.container-lg,
.container-md,
.container-sm,
.container-wd,
.container-xl,
.max-w-md-1160
{
	max-width: none!important;
}

.container,
.container-fluid,
.container-lg,
.container-md,
.container-sm,
.container-wd,
.container-xl
{
	width: 98%!important;
}

.videoCentered
 {
    width: 87%!important;
}

}

@-moz-document domain("videoshub.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

[class*='text-gray-'],

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.font-size-21,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.font-size-12 span:nth-child(2),
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("videoshub.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.bg-gray-6820
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.col-lg,
.custom_pagination .page-item:not(:last-child) .page-link,
.product-image a:hover::after,
.sidebar-area::before,

[class*='bg-gray-'],
main
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.header-section
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.h-bg-1 li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.custom_pagination .page-item:not(:last-child).active .page-link
{
	background: radial-gradient(ellipse at center, #00809d 0%,#000 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("videoshub.com") {
/* Image Settings */

.mb-3,
.mb-4d,
.pr-2,
.px-2
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.mb-3:hover,
.mb-4d:hover,
.pr-2:hover,
.px-2:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img:not([src="/static/theme/assets/img/mainlogo.png"])
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

}

@-moz-document domain("videoshub.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


/* Clock ___________________________________________________________________________*/

.fa-clock:before {
    content: "";
}

.fa-clock:before
{
	background: url(https://i.ibb.co/nfDfPPb/clock.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-clock:before
{
    background-size: 23px!important;
    height: 24px!important;
    padding-left: 24px!important;
    width: 10px!important;
}

/* Eyes _______________________________________________________________*/

.fa-eye:before {
    content: "";
}

/* Open ____________________________*/

.fa-eye:before {
	background: url(https://i.ibb.co/VSLvZzD/eye-clip-art-symbol-eye-makeup-brown.png) no-repeat!important;
	box-sizing: border-box;
}

/* Close ____________________________*/

Selector {
	background: url(https://i.ibb.co/v3qK5Rf/no-see.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-eye:before {
    background-size: 25px!important;
    padding-left: 26px!important;
    width: 10px!important;
}

/* History __________________________________________________________________________________*/

.fa-history:before {
    content: "";
}

.fa-history:before
 {
	background: url(https://i.ibb.co/XYpPJXx/recurring.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-history:before
 {
    background-size: 25px!important;
    padding-left: 26px!important;
    width: 10px!important;
}

/* Search ___________________________________________________________________________*/

[id="searchProduct1"] svg
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

[id="searchProduct1"] svg
{
	background-size: 30px!important;
	height: 31px!important;
	padding-left: 31px!important;
	width: 1px!important;
}




}

@-moz-document domain("videoshub.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
