css:
@-moz-document url("https://www.google.com/")
{

/*background color*/
  body, input, button
  {
    background-color: #ffffff;
  }


/*searchbar when not active*/
  .RNNXgb
  {
    background: #ffffff;
    border: 1.5px solid  #eee;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /*searchbar hovere*/
  .RNNXgb:hover
  {
    background-color: #ffffff;
    border: 1.5px solid #eee;
  }

/* search buttons color*/
  .lJ9FBc input[type="submit"], .gbqfba
  {
    background-color: #fff0;
    border: 1px solid #0000;
    border-radius: 4px;
    color: #808080;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /*search menu color */
  .aajZCb
  {
    background: #ffffff;
    font-family: "Lucida Console", "Courier New", monospace;
  }
  
  /*google logo opacity 0 */
  .lnXdpd
  {
    opacity: 0;
  }

/*replacement logo */
  .k1zIA
  {
    background-image: url(https://upload.wikimedia.org/wikipedia/commons/thumb/8/8d/Google_logo_%282010-2013%29.svg/2560px-Google_logo_%282010-2013%29.svg.png) !important;
    background-repeat: no-repeat;
    background-size: 85%;
    background-position: 50% 100%;
  }

  /*hight of logo */
  .rSk4se
  {
    max-height: 150px !important;
  }


/* search links color */
  .WggQGd
  {
    color: #000000 !important;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /* search text color */
  .wM6W7d
  {
    color: #eee;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /* language text color*/
  a
  {
    color: #898989;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /* "google offered in" text */
  #SIvCob
  {
    color: #5e6062;
    font-family: "Lucida Console", "Courier New", monospace;
  }
 
  /* gmail images to right color */
  a.gb_f, span.gb_f
  {
    color: #898989;
    font-family: "Lucida Console", "Courier New", monospace;
  }

  /* button 1 */
  .RNmpXc
  {
   border: 3px solid #A9A9A9;
    font-family: "Lucida Console", "Courier New", monospace;
  }
   /* button 2 */
  .gNO89b
  {
   border: 3px solid #A9A9A9;
     font-family: "Lucida Console", "Courier New", monospace;
  }
}
