css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("classicnickshows.info") 
{
/* General */
body {background-color:#517CA1 !important}
#punmisc div#brdmenu.inbox {border-color:#F0A06A !important}
#punmisc div#brdtitle.inbox {border-color:#517CA1 !important}
li.maintenancelink a:link,li.maintenancelink a:visited,li.maintenancelink a:hover {color:#00ff00 !important}
.pun a:link {color:#ff7200 !important}
.pun a:hover {color:#BF5600 !important}
.pun a:visited {color:#1F2F3D !important}
.pun blockquote,div.codebox,div.forminfo,div.blockpost label {background-color:#FFC699 !important;border-color:#ff7200 !important}
.pun div>fieldset {border-color:#3E607D !important}
.pun h2 {background-color:#36526B !important;color:#FFFFFF !important}
.pun h2 a:hover,#brdmenu a:hover {color:#EE8D4E !important}
.pun hr {background-color:#36526B !important;color:#36526B !important}
.pun legend {color:#000000 !important}
.pun td,.tclcon {border-color:#FFFFFF !important}
.pun th {background-color:#5B86AC !important;border-color:#2D78B2 #2E7CB8 #2F7FBD !important}
.pun,.pun div.blockmenu li.isactive a,#posterror li strong {color:#1A2733 !important}

/* Header */
#brdmenu {background-color:#39566F !important}
#brdtitle {background-color:#517CA1;color:#ff7200 !important}
#brdtitle p,.pun h2 a:link,#brdmenu a:link,#brdmenu a:visited {color:#E3F5FC !important}
#brdmenu a:hover {color:#ff7200 !important}
#brdwelcome,#brdstats,#brdfooter,.blockform div.box,#msg div.box,.blockpost div.inbox,#helpfile {border-color:#DFE6ED #00ff00 #CBD1D8}

/* Space */
#adminconsole td,#adminconsole th,#punmisc #brdfooter div.inbox {border-color:#B5E7FA !important}
div.blockpost div.box {border-color:#006fC9 #0066B9 #0066B9 !important}
div.blockpost div.box,div.postright,div.postfootright {background-color:#D4F1FC !important}
div.blockpost h2 {background-color:#6DCFF6 !important}
div.blockpost h2 span.conr {color:#000000 !important}
div.box {border-color:#0066B9 !important}
div.box,#adminconsole fieldset th,.rowodd,.roweven,#brdwelcome,#brdstats,#brdfooter,#postpreview,#viewprofile dd,div.forminfo,#adminconsole fieldset td,div.blockmenu div.box,#adstats dd {background-color:#B5E7FA !important}
div.box,div.postfootleft,div.postfootright,div.blockmenu div.inbox,div#punredirect .inbox,#punmisc div.inbox,div#adintro,div#adstats {border-color:#ff7200 #D66000 #E66700 !important}
div.postfootleft {border-bottom-color:#B5EBFF !important}
div.postleft,div.postfootleft,div.blockpost label {background-color:#B5EBFF !important}
div.postright,div.postfootright {border-left-color:#B5EBFF !important}
li.postreport a:hover,tr.iclosed td.tcl a:hover {color:#AAA !important}
li.postreport a:link,li.postreport a:visited,tr.iclosed td.tcl a:link,tr.iclosed td.tcl a:visited {color:#888 !important}
tr.iredirect div.icon {border-color:#f4f4f4 !important}
tr.isticky {background:#6DCFF6 !important}

/* Icon */
div.icon{border-color:#EDF1F5 #DDE0E4 #C6CBD3 #BABFC6}
div.inew{border-color:#2B75AD #235E8C #1F537B #266799}
}
