css:
@-moz-document domain("lichess.org")
{
html {
  color: #F0F0F0;
}

.lpools>div {
    color: #F0F0F0;
}
  
.lobby__box .user-link {
    color: #F0F0F0;
}
 
#topnav section>a {
    color: #F0F0F0;
}
  
.site-title span {
    color: #F0F0F0;
}
  
.site-title a {
    color: #F0F0F0;
}
  
.lobby__box__top .title {
    color: #F0F0F0;
}
  
.lobby__box td.name a {
    color: #F0F0F0;
}
  
body {
    color: #F0F0F0;
}
  
.tabs-horiz, .lobby__counters, .lobby__streams .stream, .timeline, .lobby__about a, .timeline .entry a {
    color: #F0F0F0;
}
  
.button.button-metal, .button.button-metal:hover {
    color: #F0F0F0;
}

.tour-spotlight {
    color: #F0F0F0;
}

.lobby__streams .more, .timeline .more {
    color: #F0F0F0;
}

.mini-game, mini-game:hover {
    color: #F0F0F0;
}

.lobby__puzzle .text {
    color: #F0F0F0;
}

.lobby__counters a {
    color: #F0F0F0;
}

.site-buttons .link {
    color: #F0F0F0;
}

.ublog-post-card__title {
    color: #F0F0F0;
}

.lobby__box__top .more {
    color: #F0F0F0;
}


.ceval help, .ceval .engine {
    color: #F0F0F0;
}

.copyables .name {
    color: #F0F0F0;
}

.subnav a {
    color: #F0F0F0;
}

.user-link {
    color: #F0F0F0;
}
}
