@-moz-document domain("trimps.github.io") {
.battleCell {
    height: 38px;
} 



.cellColorNotBeaten {
    background: -webkit-linear-gradient(top, #484848 75%,#383838 95%);
}

.spireCell.cellColorNotBeaten {
    background: -webkit-linear-gradient(top, #5c5779 75%,#403c54 95%) !important;
}
.cellColorNotBeaten.Corruption {
    background: -webkit-linear-gradient(top, #33095b 75%,#23063f 95%) !important;
}
.cellColorNotBeaten.Healthy {
  background: -webkit-linear-gradient(top, #493519 75%,#312311 95%) !important;
}
.cellColorNotBeaten.Magma {
  background: -webkit-linear-gradient(top, #8f0000 65%,#703c00 95%) !important;
}
.cellColorNotBeaten.TrimpmasSnow {
    background: -webkit-linear-gradient(top, #6082bc 10%, #484848 60%, #484848 75%, #383838 95%);
}
.cellColorNotBeaten.CorruptSnow {
