    background: -webkit-linear-gradient(top, #703c00 0%, #6082bc 10%, #484848 60%, #484848 75%, #383838 95%);
}


.cellColorBeaten {
    background: -webkit-linear-gradient(top, #194A19 75%, #154214 95%) !important;
}
.cellColorBeaten.Corruption {
    background: -webkit-linear-gradient(top, #194A19 85%,#33095b 100%) !important;
}
.cellColorBeaten.Healthy {
  background: -webkit-linear-gradient(top, #194A19 75%,#312311 95%) !important;
}
.cellColorBeaten.Magma {
    background: -webkit-linear-gradient(top, #194A19 65%, #7a3c00 95%) !important;
}



.cellColorOverkill {
    background: -webkit-linear-gradient(top, #3E4A19  75%,#323b14 95%) !important;
}
.cellColorOverkill.Corruption {
    background: -webkit-linear-gradient(top, #3E4A19 85%,#33095b 100%) !important;
}
.cellColorOverkill.Healthy {
  background: -webkit-linear-gradient(top, #3E4A19  75%,#312311 95%) !important;
}
.cellColorOverkill.Magma {
    background: -webkit-linear-gradient(top, #3E4A19  65%,#7a3c00 95%) !important;
}





#mapGrid {
    background: rgba(0,0,0,0) !important;
}
#mapGrid .cellColorNotBeaten.voidCell {
    background: -webkit-linear-gradient(top, #33095b 75%,#23063f 95%) !important;
}
#mapGrid .cellColorNotBeaten.voidCell.CorruptSnow {
  	background: -webkit-linear-gradient(top, #33095b 0%, #6082bc 5%, #33095b 40%, #33095b 75%, #23063f 95%) !important;
}

.battleCell.empoweredCellPoison.cellColorNotBeaten {
    background: -webkit-linear-gradient(top,rgba(60, 180, 60, 0.75) 70%, rgba(45, 135, 45, 0.75) 100%) !important;
}

.battleCell.empoweredCellWind.cellColorNotBeaten {
    background: -webkit-linear-gradient(top,rgba(72, 90, 156, 0.75) 70%, rgba(54, 68, 117, 0.75) 100%) !important;
}

.battleCell.empoweredCellIce.cellColorNotBeaten {
    background: -webkit-linear-gradient(top,rgba(60, 60, 240, 0.75) 70%, rgba(45, 45, 180, 0.75) 100%) !important;
}

.battleCell.cellColorCurrent, .battleCell.empoweredCellIce.cellColorCurrent, .battleCell.empoweredCellPoison.cellColorCurrent, .battleCell.empoweredCellWind.cellColorCurrent {
    background: -webkit-linear-gradient(top, #605020 70%, #5e3f1f 95%) !important;
