css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix("http://en.wikipedia.org/"),url-prefix("http://tr.wikipedia.org/") {


body, #searchInputSuggest,td[class*="navbox"]{ font-family: Verdana,georgia, serif !important; background: #212121 !important;color:white!important; }



#content { padding: 3em 4em 3em !important; margin: 0 !important; border: 0 !important; background-color:#212121 !important;color:#999 !important;}
 
div#siteNotice,#siteSub,#footer,#toc,#administrator,#coordinates,.editsection,#spoken-icon,#featured-star,#donate,#anontip,.ambox,#protected-icon,#p-logo,#p-cactions,#p-personal,#p-navigation,#p-interaction,#p-tb,#p-lang,#p-search h5,#searchGoButton,#mw-searchButton,#anon-banner{ display: none !important; }
p, ul, dl { margin-bottom: 1.7em !important; }
ul { margin-bottom: 2.2em !important; }
ul ul { margin-bottom: 1em !important; margin-top: .8em !important; }
h1 { font-size: 150% !important; padding-bottom: 10px !important;  font-weight: bold !important; padding-right: 280px !important;}
h2 { font-size: 110% !important; padding-bottom: 15px !important; margin-top: 40px !important; font-weight: bold !important; }
#mp-upper h2, #mp-tfp h2 { margin-top: 0 !important; padding-top: .6em !important; }
h3 { font-size: 110% !important; padding-bottom: 5px !important; margin-top: 40px !important; }
h4 { font-size: 110% !important; padding-bottom: 5px !important; margin-top: 40px !important; }
p, li { font-size: 95% !important;}
dd, dt { font-size: 110% !important; }
.infobox { margin-left: 2.5em !important; margin-bottom: 3em !important; border-style: dotted !important; padding: .5em !important; width: 200px !important; border-color: #999 !important;background-color:#212121!important;color:gray !important; }

#contentSub, #contentSub2 { margin-left: 0 !important; }
div.tleft { margin: 0 4em 2em 0 !important; }
div.tright { margin: 0 0 2em 4em !important; }
li { margin-bottom: 1em !important; margin-left: 2em !important; line-height: 1.5em; }
li ul li { font-size: 100% !important; margin-bottom: .6em !important; }
div#catlinks { margin-top: 4em !important; padding-left: 1em !important;  padding-right: 1em !important;   }
div.dablink { padding-left: 0 !important; }
.messagebox { margin: 0 0 1em !important; text-align: left !important; padding: 1em !important; }
dt { margin-top: 1em !important; }
h1, h2 { border-bottom-style: dotted !important; }
#content blockquote, table.cquote { margin-left: 4em !important; margin-right: 4em !important; margin-bottom: 2em !important; line-height: 2.2em !important; background: #efefef !important; padding: 1.4em 2em 0.1em !important; -moz-border-radius: 10px !important; }
table.cquote { font-size: 1.4em !important; line-height: 1.4em !important;  padding: 1.4em .8em !important; border-collapse: separate !important; }
table.cquote p { font-size: 1em !important; }
a:link{color:#88F !important;}
a:hover,a:active{color: white !important;}
input#searchInput { -moz-appearance: none !important; border: 1px dotted gray !important; font-size: 125% !important; padding: 6px 12px 4px !important; margin: 5px !important; color: #b5b5b5 !important; width: 15em !important;}
input#searchInput:focus { border-color: #7f7f7f !important; color: white !important;}
.catlinks { -moz-border-radius: 5px !important; }
div.thumbinner{background-color:#212121!important;border: 1px}
.catlinks { -moz-border-radius: 5px !important; }
div#searchBody.pBody { display: block !important; padding: 0 !important; border: 0; border: 0 !important; background: none !important; }
#p-search { position: absolute !important; top: 2.6em !important; right: 3.3em !important; width: 21em !important;}
table#mp-topbanner { margin-top: 6em !important; }
th,div.thumbinner{border: 1px dotted #333 !important;}
h1, h2, h3, h4, h5, h6{color: gray !important;text-shadow: 1px 1px 1px #444;font-weight:normal!important;font-family:Candara}
div[class="thumb tright"],.noarticletext,.toccolours,table.navbox {background-color:#212121!important;border:1px dotted gray!important;color:gray !important}
table,#noarticletext{background-color:#212121!important;color:gray !important}
pre,code{background-color: #223 !important;}
th{background-color:#212121!important;border:1px dotted gray !important}

.wikitable,#content blockquote, table.cquote,.catlinks,.navbox-title, table.navbox th{background-color:#272727 !important;}
#p-search{opacity: 0;}
#p-search:hover{opacity: 1;}
td{font-size: 12px !important;}
}
