css:
@-moz-document regexp(".*mariadb.com/kb/.*") {
/* Light Text */
 body.mpkb #top-nav ul li a, body, body.mpkb div#main, body.mpkb .navbar, body.mpkb #navbar-bottom .main-menu .nav a {
    color: #EEE;
}
/* Colored Text */
 .creole code {
    color: #CCF;
}
/* Dark Background */
 .breadcrumb, .well, div.product h5.product_title {
    background-color: #222;
}
/* Lighter Background */
 body, body.mpkb div#main, body.mpkb .navbar {
    background-color: #333;
}
/* custom / Important */
 aside .actions a, aside .ask_button a {
    background-color: #333 !important;
    color: #EEE !important;
}

.creole pre.fixed, body.mpkb #content_disclaimer, #comments div.comment div.comment_actions, .creole .datetime, body.mpkb .section_navigation a:hover, .nav > li > a:hover, .nav > li > a:focus, #comments div.comment div.info {
    background-color: #222;
    color: lightcyan;
}

ul.listing .node a:hover {
    background-color: transparent;
}

ul.listing a:hover {
    color: inherit;
}

ul.listing .media-heading {
    color: lightblue;
}

.creole pre.fixed, .well, body.mpkb aside ul.nav li a, #comments div.comment {
    border: 1px solid #555;
}

body.mpkb .section_navigation li.parent a {
    border-bottom: #222 2px solid;
}
}
