@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ling.pl") {

  body { 
    background: #c9d3d6 !important; 
    color: #c9d3d6 !important; 
    padding-top: 9px !important;
  }
  body > center > table { background: #fff !important; }

  table, tbody, th, tr, td, tfoot { display: block !important; }
  body > center > table[width="767"] {
    width: 550px !important;
  }
 
  body > center > table[width="767"] > tbody > tr > td[style^="padding-top"] {
    padding-top: 0 !important;
  }
  body > center > table[width="767"] > tbody > tr > td > table[width="754"] {
    background: #fff !important;
    width: 548px !important;
    overflow: hidden !important;
    padding: 0 10px 10px 10px !important;
    margin: 0 auto 0 0 !important;
    left: 0 !important;
    position: static !important;
    clear: both !important;
  }

  body > center > table[width="767"] > tbody > tr > td > table[width="754"] table[width="500"] {
    width: 525px !important;
  }

  a[href="http://www.ling.pl/content.php4?name=toolbar"],
  body > center > table > tbody > tr > td > table[width="767"],
  body > center > table > tbody table[width="754"] td[valign="top"] > table[width="147"],
  table[width="754"][height="20"], table[width="754"][height="18"], table[gcolor],
  table[background="http://www.ling.pl/top-podred.jpg"],
  #szuk table > tbody > tr > td[style^="padding-top: 5px;"],
  body > center > table > tbody > tr > td[align="center"],
  body > center > h1, span.dictsmallprint, span.dictword a { 
    display: none !important; 
  }

  #resnode + center > table,
  #resnode + center > table > tbody {
    width: auto !important;
  }

  #szuk {
    display: block !important;
    width: 546px !important;
    float: left !important;
    margin-top: -10px !important;
  }

  #szuk > table {
    background: #576f74 !important;
  }

  #szuk td {
    color: #fff !important;
  }

  #szuk table > tbody > tr > td[width="306"] {
    display: none !important;
  }

  #szuk table > tbody > tr > td[style^="padding-left"] {
    height: auto !important;
    padding-left: 0 !important;
    position: static !important;
  }

  #szuk table > tbody > tr > td[style^="padding-left"] span[style^="background"] {
    display: block !important;
    padding: 10px !important;
    width: 100% !important;
    height: auto !important;
    line-height: 100% !important;
    background: transparent !important;
    font-size: 14px !important;
    font-weight: bold !important;
  }

  #szuk table > tbody > tr > td[style^="padding-left"] img,
  #szuk table > tbody > tr > td[style^="padding-left"] span[style^="position"] {
    display: none !important;
  }

  #szuk table > tbody > tr > td[style^="background"][align] {
    height: auto !important;
    background: #d8dfe1 !important;
    color: #6e8286 !important;
    padding: 0 10px 5px 10px !important;
    clear: both !important;
  }

  #szuk input[type="radio"] {
    position: relative !important;
    top: 2px !important;
  }

  #szuk table > tbody > tr > td[style^="padding-bottom"] {
    padding-bottom: 10px !important;
  }

  #szuk table > tbody > tr > td[align="center"] {
    text-align: left !important;
  }

  #szukWord {
    width: 400px !important;
    margin-left: 10px !important;
    margin-bottom: 10px !important;
    padding: 4px 5px !important; 
    height: auto !important;
  }

