css:
@-moz-document domain(lyrania.co.uk) {
    #leftinfo,#chat_row,#header,#side1,#side2,#loadoutpotions,#mainnav,#popupcontainer,#content div b{
    display: none;
}
#content {
    margin: auto;
}
#content input {
    left: 30% !important;
}
#rightinfo {
    top: 25px;
    left: 60% !important;
}
#rightinfo div {
    top: 60px
}
#holder {
    width: 100%;
    margin: 0;
    padding: 0;
}
#main {
    font-size: 0;
    width: 100%;
    border: none;
    margin: 0;
    text-align: center;
}
.mapContainer {
    font-size: 16px !important;
}
.flex-content {
    max-width: 100%;
}
.lrow {
    display: inline;
}
.strong,#autosleft, #mobsleft,font,span,#stopauto {
    font-size: 1rem;
    color: white !important;
    display: inline;
}

#mobsleft {
    padding-left: 10px
}
#autosleft:before {
    content: "A"
}
#mobsleft:before {
    content: "M"
}
.strong {
    font-size: 0;
    padding-right: 10px;
}
.bonus,#levelgain {
    display: none;
}
}
