    .btnImageIcon.icon_remove.btnImage.OmbreFocus:hover {
      color: red;
      transition: all 0.3s;
    }

    .ibe_image_etab img {
      display: block;
      margin-left: 5px;
      width: 32px;
      height: 32px;
    }
    ObjetCelluleDate.input-wrapper

    .ibe_image_etab img:hover {
        width: 34px;
      height: 34px;
      margin-left: 4px;
      transition: all 0.3s;
    }

    #GInterface_T, .AffichagePageAccueil.dotty.eleve, .widgets-global-containerm, .AlignementHaut.Espace {
        background: #333 !important;
    }

    .message-conteneur span, #id_376, #id_356, #id_331, #id_275, #id_248, #id_201, #id_164, .Gras.AlignementMilieu.GrandEspaceHaut, #id_125, #id_415, #id_221, .AlignementDroit.PetitEspaceDroit, .NoWrap.titre-entete, .AlignementMilieu.Insecable.Maigre div {
      color: white !important;
    }

    .ocb_cont.as-input.as-date-picker div, .ie-ellipsis {
      color: black;
    }

    .Fenetre_Bordure.ConteneurFiches, .listitem, .ObjetCelluleDate.input-wrapper i {
      background: #555 !important;
      border-radius: 2px;
      border: 2px solid #444;
    }

    .ObjetCelluleDate.input-wrapper i:hover {
      background: #666 !important;
      border: 2px solid #555;
      transition: all 0.3s;
    }

    .ObjetBandeauEspace {
      background: #333;
      color: white;
    }

    .icon_off.colorFoncee {
      color: red;
    }

    .icon_off.colorFoncee:hover {
      color: #990000;
    }

    .as-button.bt-widget.icon.icon_affichage_widget {
      color: white;
      border: 1px solid white;
    }

    .liste_contenu_ligne, .SansMain.liste_fixed div, .zone-principale > h4 {
      background: white !important;
      color: black !important;
    }

    .as-button.bt-widget.icon.icon_affichage_widget:hover {
        color: black !important;
        border: 1px solid black;
      transition: 0.8s all;
    }

    .ibe_image_etab img, .BlocDevoirEvaluation, #id_9_contenu, .SansMain.liste_fixed {
      border-radius: 3px !important;
    }

    .Image_DeploiementListe_Deploye, .Image_DeploiementListe_NonDeploye {
      height: 8px;
      width: 8px;
      background: url("https://0771997c.index-education.net/pronote/E_3_C_FBEC285955D8F6AE2174E9C3FCBC0569CA069511EE560D7086A397B2101D7AA9_L_1036/images/DeploiementListe.png") 0 0 no-repeat !important;
    }

    .liste_contenu_cellule_contenu, .zone-principale h4, .Gras.InlineBlock.PetitEspaceHaut.PetitEspaceBas.EspaceDroit.EspaceGauche10 {
      color: white;
      background: #555;
      border-radius: 5px;
    }
}
