/* DO NOT EDIT BELOW THIS LINE */
  :root {
    --teflon: rgb(238, 238, 238);
    --teflon-70pc: rgba(0, 0, 0, .8);
    --white-70pc: var(--teflon-70pc);
    --whitey: rgb(28, 28, 28);
    --warm-gray-30pc: rgb(1, 1, 1);
    --middle-gray: rgba(36, 36, 36, 1);
    --casper: var(--whitey);
    --warm-gray: rgb(0, 0, 0);
    --grid-bg: rgb(18, 18, 18);
    --grid-dots: var(--middle-gray);
    --karl: rgb(15, 15, 15);
  }

.dotComments * {
    background-color: #000000!important;
}
.screenThumbnailSectionDividerWrapper .screenInfo, .screenThumbnail.dashboardScreen {
 background: #121212;   
}

.versionsSidebarHeader + div, .filterNoteSubmenuOptionItem, .filterNotesDropdown.widget, .filterNotesDropdown.widget:hover, .versionsSidebar .versionsSidebarHeader, .newTagMenuHeader {
    background: #000000!important;
}

.screensSortOptions, .elevatorContent, .popoverContent *, .styleguideLeftSidebar  {
    background: #090909;
    background-color: #090909;
}
.popoverContainer .workflowStatusFilter {
    background-color: #090909!important;
}
.screensSortDropdown:active , .sectionNavigationMenu {
    background-color: #121212!important;
}
.elevatorContent div div div:hover, .sectionNavigationMenu div a:hover, .inviteButton:hover, .popoverContent a:hover *,.popoverContent .noSelect:hover, .popoverContent .noSelect:hover *, div[title='Sort by Sections'], div[title='Sort by Sections']:active {
    background-color: #202020!important;
}
.activityItem, .popoverContainer a div {
    background: #121212!important;
    color: #ffffff;
}
div .dropdownOptionsBackdrop div {
    background: #121212!important;
    color: #ffffff;
}
.overlayZplMenuContainer * {
    background: #121212;
}

  body {
    background-color: #121212;
  }
  .detailSections {
    background: #121212;
  }
  .screenSidebar {
    background: #121212;
  }
  .barrelSidebar, .sectionActions svg, .contextMenu .menuitem, .barrelSidebarSeparator {
    background: #090909;
  }
  .sectionMenu hr {
    background: white;
  }

  #allProjects .project .projectInfo,
  .memberRow:nth-of-type(2n+1),
  .versionsSidebar,
  .versionNode,
  .version .commit,
  dialog,
  dialog.dialog,
  .loadingAnimationBar>.whiteBar,
  header,
  .extensionDialog,
  .projectExtensions .extensionListItem,
  #manageTagsPopup,
  #planChooser,
  #inviteUserPopup,
  #inviteUserPopup .inviteeListAlreadyAdded,
  #inviteUserPopup .inviteeListEmpty,
  #inviteUserPopup .userList {
    background-color: var(--whitey);
  }

  header,
  .zplSidebarMenu,
  .zplSidebarMenu .submenu,
  #allProjects .project,
  .userMenuContainer,
  .notificationsContainer,
  .tooltip,
  dialog,
  dialog.dialog,
  #manageTagsPopup,
  #inviteUserPopup,
  .menu {
    box-shadow: 0 2px 4px 0 #000;
  }

  .projectSidebar .slackButton {
    background-color: #000;
    border-color: #333;
  }

  .supportButton .supportPopup {
    background-color: #000;
  }

  .supportButton .supportPopup a span {
    text-shadow: none;
  }

  input[type="search"],
  input[type="text"] {
    background: transparent;
  }

  .userMenuContainer,
  .notificationsContainer,
  .zplSidebarMenu,
  .zplSidebarMenu .submenu,
  .menu,
  dialog.dialog form.buttonsContainer button {
    background-color: var(--middle-gray);
  }

  #dotgrid,
  .dotgrid {
    background-image: linear-gradient(transparent 0px, transparent 2px, var(--grid-bg) 2px, var(--grid-bg) 20px), linear-gradient(to right, var(--grid-dots) 0px, var(--grid-dots) 2px, var(--grid-bg) 2px, var(--grid-bg) 20px) !important;
  }

  .tooltip {
    background-color: #324a69;
  }
  }
