@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yourworldoftext.com") {


/* Background-color for menus */

.ui-vis, .menu, #nav, #nav li.hover, #simplemodal-container, .ui, #coords, #paused {
background-color: rgba(30,30,30, 0.1) !important;
background: none 0 0 rgba(30,30,30, 0.5) !important;
}

/* Text Color */

.menu, #nav, #coords, #paused {
  color: rgb(255,255,255) !important;
}

/* border color */

#nav, #coords, #paused {
  border: 1px solid rgb(0,255,0) !important;
}

/* Tile Background color */

.tilecont, #yourworld table {
  background: rgb(100,100,100) !important;
color: rgb(40,40,40) !important;
}


/* Menu-1 hover */
.ui {
  color: rgb(0,255,0) !important;
  font-family: Verdana !important;
  font-size: 12px  !important;
}

/* Menu-2 Hover */
#nav li.hover {
   color: rgb(255,255,0) !important;
}
 
#menu.hover {
  color: rgb(0,255,0) !important;
  border: none !important;
}



/* Coordinates box */
#coords {
   font-family: Verdana !important;
   font-size: 14px  !important;
   padding: 4px !important;
   margin:50% 50% 62% 10px !important;
   text-align:center !important;



}

/* The Yellow box */
td[style="background-color: yellow;"]  {
 background-color: rgb(0,255,0) !important;
}



.protected {
 background-color: rgb(255,0,0) !important;
background: rgb(255,0,255) !Important;
}


}
