css:
/* i really want this to be global */
h1 {
    font-size: 180%;
    font-weight: bold;
    margin: 25px 20px;
    line-height: 1.5em;
    font-style: italic;
    font-family: monospace;
}
th, td.th, table.grid td.th, table.grid tbody tr:nth-child(odd)>td.th, table.grid tbody.tbody tr:nth-child(odd) th.th {
    font-weight: bold;
    color: #ffe9e9;
    padding: 4px 5px;
    background: #484848;
}
#attByClass {
    font-weight: bold;
    color: #444;
    padding: 4px 5px;
    background: #7e7e7e;
    font-size: 100%;
}
.psparent #nav-main a {
    height: 3em;
    padding-left: 52px;
    padding-right: 20px;
    color: #ffe7e7;
    text-indent: 0;
    display: table-cell;
    vertical-align: middle;
    background-repeat: no-repeat;
}
body {
    font-family: Arial,sans-serif;
    font-size: 14px;
    line-height: 1.5em;
    color: #fff;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    background-color: #2c2c2c;
    background-image: -webkit-gradient(linear, left top, right top, color-stop(0, #f4f7fa), color-stop(200px, #f4f7fa), color-stop(201px, #ccc), color-stop(202px, #fff));
    background-image: -o-linear-gradient(right, #f4f7fa 0, #f4f7fa 200px, #ccc 201px, #fff 202px);
    background-image: -moz-linear-gradient(right, #f4f7fa 0, #f4f7fa 200px, #ccc 201px, #fff 202px);
    background-image: -webkit-linear-gradient(right, #f4f7fa 0, #f4f7fa 200px, #ccc 201px, #fff 202px);
    background-image: -ms-linear-gradient(right, #f4f7fa 0, #f4f7fa 200px, #ccc 201px, #fff 202px);
    background-image: linear-gradient(to right, #000000 0, #0a0a0a 200px, #ccc 201px, #fff 202px);
    background-repeat: repeat-y;
    background-size: 202px 10px;
    background-position: -20px top;
}
