@-moz-document domain("reprap.org") {

body {
  background-color: rgb(49, 50, 50);

  background-image: url("https://hackaday.com/wp-content/uploads/2016/03/repraponedarwin-darwin.jpg");
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-size: cover;
  background-position-y: 5%;
  backdrop-filter: blur(4px) brightness(50%);

  padding-top: 10px;
  padding-bottom: 20px;
  margin: 0px;
}

body
, #phorum {
    color: #d5d8d5;
}

#phorum #logo {
  filter: brightness(90%);
  background-image: none;
}

#phorum {
  font-family: tahoma, sans-serif;
  font-size: 0.84em;
  font-weight: 400;
  line-height: 1.2;

  max-width: 1100px;
  margin: auto;
  min-height: 98vh;
}

#phorum a {
  color: #8bd28b;
  text-decoration: none;
}

#phorum a:hover {
  color: #8bf28b;
  text-decoration: underline;
}

#phorum #post-body {
  background-color: #edf2ed;
}

#phorum textarea
, #phorum table.list td {
  background-color: #485244;
}

#phorum table.list td.alt {
  background-color: #474842;
}

#phorum textarea.body {    
  color: #d5d7d5;
}

#phorum div.generic
, #phorum #thread-options {
  padding: 8px;
  background-color: #487040;
  border: 1px solid #4d894d;
    border-bottom-color: rgb(77, 137, 77);
    border-bottom-style: solid;
    border-bottom-width: 1px;
  overflow: hidden;
}

#phorum div.message-body {
  padding: 16px;
  margin: 0 0 16px 0;
  border: 1px solid #4d894d;
    border-top-color: rgb(77, 137, 77);
    border-top-style: solid;
    border-top-width: 1px;
  background-image: none;
  background-color: #485244;
}

div.message-body ol li {
    padding-bottom: 10px;
}

#phorum #post-body
, #editor-tools .editor-tools-button
, input {
  background-color: #dde2dd;
}

input {
    border: 1px
}

input:focus,
select:focus,
textarea:focus,
button:focus {
    outline: none;
}
}
