css:
@-moz-document domain(4chan.org), domain(boards.4channel.org), domain(4channel.org){

.thumb {border-radius: 7px;}
body {
  background: linear-gradient(to top, #4e434a  , #2e1e2a);
      background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
  	color:#efade4
}
span{  color:#efade4;}
div.boardTitle{color:#efade4;}
div#togglePostFormLink.desktop{  color:#efade4;}
div#ctrl-top.desktop{  color:#efade4;}
td{  color:#efade4;}
.c-thread img {border-radius: 10px}
.top-box{background:#e3d1df;}
.boxbar {border-radius: 10px; background:#e5869c;}
.box-top{  border-radius: 10px;}
.box-outer{  border-radius: 10px;}
.teaser {text-shadow: 0 0 5px rgb(0 0 0 / 10%);}
div.boardBanner>div.boardTitle{font-family: 'Segoe UI', SANS-SERIF;}
quote-preview{background:#e3d1df; color:#efade4}
hr{border-top: 1px solid #783a65;}
.button{color:#9797c7}
.yotsuba_new .postMenuBtn, .futaba_new .postMenuBtn {color:#9797c7}
.warning {color:#b7117c}
div.post div.postInfo span.postNum a{color:#f5d5ef}
div#boardNavDesktop a{color:#9797c7;}
div.pagelist strong {color:#efade4}
div#boardNavDesktopFoot a  {color:#efade4}
.c-teaser{color:#b74766}
.c-board{color:#cb69ad}
h3{color:#cb69ad}
a{color:#9797c7}
a.replylink:not(:hover){color:#9797c7!important;}
div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover{color:#bd51bf!important;}
a:hover{color:#6585cb!important}
a:visited{color:#a36d9a!important}
a.quotelink{color:#9797c7!important}
.reply:target {background:#483b58!important}
.reply.highlight {background:#483b58!important}

.extended-small .thread{  height: 320px;}

div.thread {
   background: #4a3340;}

.thread {
  padding-bottom: 20px;
  border-radius: 10px;
  color: #ddbfd4;
  margin: 10px;
  box-shadow: 0px 2px 5px rgb(0 0 0 / 60%);
  font-family: Segoe UI,Frutiger,Frutiger Linotype,Dejavu Sans,Helvetica Neue,Arial,sans-serif; 
}
#qp.dialog,
div.post.reply {
  background:  #301822;
  border-radius: 10px;
  border: 1px solid rgba(211, 160, 181, 0.4);
  border-top: none;
  border-left: none;
  border-right: 200px;
  border-color:#b54788;
  word-wrap: normal;
margin-right: 40%;
}

/* ===HOVER REPLY===*/
#qp.dialog {
    border-color:#b54788;
  margin-right: 2%;
  min-width: 10%;
  max-width: 50%
}


img.chan-media-item{
  border-radius: 10px;
    box-shadow: 0px 2px 5px rgb(0 0 0 / 60%);
  margin-left: 10px;
  margin-right: 10px;
   max-width: 60%;

}

/*=========== 4CHAN X FIXES ============*/
div.inline {
  border-radius: 10px;
    background:  #24111a!important;
  border-color: #b54788!important;
  box-shadow: 0px 2px 5px rgb(0 0 0 / 30%);
  border-top: none;
  border-left: none;
    border-right: none;
  margin-top: 15px;
  max-width: 95%;
	width: auto;
}

.postContainer.replyContainer:nth-child(even) > div.reply {
    background-color: #301822;
    border-radius: 10px;
    box-shadow: 0px 2px 5px rgb(0 0 0 / 30%);
  
}

:root.tomorrow .qphl {
  outline: 2px solid #b54788;
}

/* ============ SCROLLBAR ============ */
html[hide-scrollbar="true"] ::-webkit-scrollbar, ::-webkit-scrollbar-corner {
    display: none;
}
:root.tomorrow .dialog {
  
  background-color:  #301822;
}


::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #301822;
    width: 10px;
    height: 10px;
}

#guide-inner-content.ytd-app::-webkit-scrollbar,
#guide-inner-content.ytd-app::-webkit-scrollbar-corner {
    background: #301822;
    width: 6px;
    height: 6px;
}

::-webkit-scrollbar-button, #guide-inner-content.ytd-app::-webkit-scrollbar-button {
    display: none;
}

::-webkit-scrollbar-thumb, #guide-inner-content.ytd-app::-webkit-scrollbar-thumb {
    background: #783a65;
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:not(:active):hover, #guide-inner-content.ytd-app::-webkit-scrollbar-thumb:not(:active):hover {
    background: #91467a;
}

::-webkit-scrollbar-thumb:active, ::-webkit-scrollbar-thumb:hover:active, #guide-inner-content.ytd-app::-webkit-scrollbar-thumb:active, #guide-inner-content.ytd-app::-webkit-scrollbar-thumb:hover:active {
    background: #783a65; }
}
