css:
@-moz-document domain(panel.simrail.eu) {

@media only screen and (max-width: 530px) {
    #map-container {height: 63vh !important;}
}

@media only screen and (min-width: 530px) {
    #map-container { height: 68vh !important;}
}


@media only screen and (min-width: 1060px) {
    #map-container { height: 75vh !important;}
}

@media only screen and (min-width: 1590px) {
    #map-container { height: 65vh !important;}
}

@media only screen and (min-width: 2020px) {
    #map-container { height: 70vh !important;}
}

@media only screen and (min-width: 2710px) {
    #map-container { height: 73vh !important;}
}

div#server-buttons {
    overflow-x: scroll;  
    display: flex;
    flex-flow: row nowrap;
}

div#server-buttons button {
    align-self: auto;
    margin-bottom: 10px !important;
}
#map-dashboard > button:first-child {
    position: absolute;
    right: 8vw;
    top: 39px;
    z-index: 9;
}

.btn-server {
    padding:  0.25rem;
}

}
