additional_info:
+ more tracks rows in window visible area;
+ highlight current played track;
+ add play & control buttons spacing;
+ fixed top menu;
+ minimize height of top area with user photo;
+ remove unsed user menu items, such us: "ПОДКАСТЫ И КНИГИ", "ДЕТЯМ", "ИСТОРИЯ" etc;

css:
@-moz-document domain('music.yandex.ru')
{

/*маркер текущего играющего трека*/
.d-track__bubble {
  position: absolute;
  top: 6px;
}

/* название трека */
.d-track__title {
  color: #aaa !important;
}
.d-track_playing .d-track__title {
  color: #fff !important;
  font-weight: bold;
}
/* 777 для описания справа от трека*/

.d-track__artists a {
  color: #aaa !important;
}
.d-track_playing .d-track__artists a {
  color: #f4ce34 !important;
  font-weight: bold;
}

.user__userpic.user__userpic_size_Huge, .d-generic-page-head__aside {
  height: 100px !important;
  width: 100px !important;
  min-height: 100px !important;
  min-width: 100px !important;
  margin: 0;
  padding: 0;
  margin-right: 20px;
  flex-basis: 100px;
}

.head-kids__left {
  display: none; 
}
.d-generic-page-head {
  display: none;
  height: 100px !important;
  min-height: 100px !important;
}
.d-generic-page-head__main {
  height: 1px; 
}
.d-subhead {
  display: none;
}

.head-kids__nav {
  position: fixed;
  top: 0;
  z-index: 1;
  width: 100%;
  height: 40px;
  white-space: nowrap;
  background: #000;
  left: 0 !important;
  padding: 0;
}
.head-kids__nav, .playlist-notification, .nav-kids a, .head-kids__controlls, .head-kids__nav, .playlist-notification, .head-kids__wrap.deco-devider, .nav-kids {
 pading: 0; 
  margin: 0;
  height: 40px;
  top: -10px;
}
.d-search.d-search_closed, .d-search.d-search_opened {
  padding-top: 20px;
}

.head-kids__search {
  flex-grow: 0 !important;
  position: fixed;
  top: -6px !important;
  left: 0px !important;
  /*background: #000;*/
  height: 76px !important;
  width: 36px;
}

.d-search__button__container {
  justify-content: flex-start !important;
  left: 0px !important;
  position: absolute;
}

.head-kids__user {
  position: fixed;
  top: 30px !important;
  z-index: 200;
  right: 0;
}

.head-container {
  height: 70px !important;
}
.head-kids.deco-pane  {
  height: 70px !important;
}
.head-kids__wrap.deco-devider {
  height: 70px !important;
}
.head-kids__wrap.deco-devider::before {
  height: 70px !important;
}
.head-kids__user {
  height: 70px !important;
}
.head-kids__subnav.theme.theme_dark {
  height: 70px !important;
}


/* убираем загрузочный экран */
.loading.deco-shade.loading_page {
  display: none;
}


/*строка с текущим проигрываемым трэком*/
.d-track.typo-track.d-track_selectable.d-track_in-lib.d-track_playing {
  background: #000;
}
.d-track.typo-track.d-track_selectable.d-track_in-lib.d-track_playing::before {
  content: " ";
  left: -4px;
  top: -1px;
  display: block;
 	height: 23px;
  border-left: 4px solid #f7dc5d;
  position: absolute;
  border-top: 1px solid #f7dc5d;
  border-bottom: 1px solid #f7dc5d;
  width: 100% !important;
  z-index: 1;
}


/* убираем отступ после списка, чтобы строки меньшей высоты отрабатывались нормально при пролистывании */
.lightlist__cont {
  padding-bottom: 10% !important;
	height: 200% !important;
  overflow-x: auto !important;
}
/* ориентация нокпки проигрывания трэка при небольшой высоте строки */
.d-button-play__wrap button {
  border: 1px solid red;
  position: absolute;
  z-index: 2;
  border-radius: 5px;
  top: -8px;
}

/* Ad blocks removing */
  .app-sms, .app-sms__content, .artist-pics__desc, .stamp, .payment-bar, .payment-show, .payment-bar__pay, .teaser, .subscription-hint, .subscription-hint_top, .overhead-wrapper, .ads-block, .ads-block__no-ads, .smalladv, .ads-block_side, .slider-hider, .sidebar-cont__adContainer, .slider-hider_ads-block, .event__notify, .audio-advert__block, .bar__new-pay, .bar__addition, .bar-below, .d-overhead, .sidebar.deco-pane, .payment-plus-popup, .overlay.overlay_shown
  {
    display: none !important;
  }

/* Убрать херню на слайдере (машинки, лыжники) */
  .winter__skater-pause, .winter__skater-play, .winter__skater-pause_left,
.bar__branding, .haval__car, .haval__car-pause
  {
    display: none !important;
  }

/* Высокий прогресс бар, легче тыкать пальцем или мышкой */
  .deco-progress .progress__bar, .deco-progress .progress__bar:hover
  {
    height: 32px !important;
    line-height: 32px !important;
  }

/* Убрать в меню Аккаунта из меню "Попробовать +" */
  li.multi-auth__line >div.multi-auth__plus
  {
    display: none;
  }

/* Убрать верхний баннер с уведомлялкой */
  div[data-b="2"], div[data-b="3"]
  {
    display: none !important;
  }

/* Расширить на весь экран */
  .page-root
  {
    max-width: 1920px;
  }

  .centerblock-wrapper
  {
    margin-right: 0px !important;
    width: 100%;
    padding-top: 0 !important;
  }

.centerblock.black.theme {
    margin-right: 0 !important;
    padding-top: 0 !important;
}

/* No max width for sidebar panel */
  .sidebar-cont, .bar
  {
    max-width: none;
  }

/* Coloring for tracklists */
  .d-track:nth-child(odd)
  {
    background: #292929;
    height: 24px !important;
  }

  .d-track
  {
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    height: 24px !important;
  }

  .d-track:last-child {
    border-bottom: 1px solid #333;
    height: 16px !important;
  }

/* Small fixes */
  .d-track_selectable:hover:after, .d-track_selectable.d-track_hover:after, .d-track_selectable.d-track_selected:after
  {
    bottom: 0px;
  }

  .sidebar-album__tracks > div
  {
    margin: 0 -13px;
  }

  .sidebar-artist__tracks
  {
    margin: -5px -18px 0px -16px;
  }

  .longlist__cont
  {
    margin: 0 -8px 0 -12px;
  }

  .d-track__start-column
  {
    margin-left: 6px;
  }

  .d-track_best:before
  {
    left: 8px;
    top: 14px;
  }

  .page-playlist__upload
  {
    border-top: 1px solid #333;
  }

  .page-album__label .link
  {
    margin-left: 10px;
  }

  .popup_page
  {
    min-height: calc(100% - 60px);
    box-sizing: border-box;
    border-left: 2px dashed;
    border-right: 2px dashed;
  }

  .sidebar-album__tracks > .sidebar-album__vol
  {
    padding: 10px 0 10px 10px;
  }
}
